INSERT INTO `products` (`product_id`, `type`, `code`, `name`, `short_description`, `detailed_description`, `price`, `total_orders`, `total_ratings`, `overall_stars`, `is_returnable`, `created_at`, `updated_at`) VALUES
('CCNPLT0000', 1, 'TUBO005', 'Cây tùng bồng lai', 'Cây tùng bồng lai tiểu cảnh chậu sứ thổ cẩm TUBO005', 'Cây Tùng Bông Lai là một loài cây mang vẻ đẹp trang nhã, với tán lá dầy xanh mướt trông giống như những đám mây và có kích thước nhỏ gọn nên rất thích hợp để bàn làm việc. Chúng là loài cây dễ trồng nên không cần phải tốn công chăm sóc, tuy nhiên cần đặt tại nơi có nhiều ánh sáng.\r\n*Giá bao gồm chậu, đĩa lót và sỏi rải mặt', 500000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0001', 1, 'CPTK001', 'Phát tài bộ – Thiết Mộc lan', 'Cây phát tài bộ 5 – Cây thiết mộc lan CPTK001', 'Cây phát tài bộ còn được biết đến với tên gọi khác là cây thiết mộc lan. Loài cây nội thất được đánh giá là đem lại nhiều sinh khí, may mắn và thịnh vượng cho gia chủ, nhất là khi cây nở hoa là dấu hiệu tiền tài đang đến với bạn. Hơn nữa, nếu bạn đặt cây theo hướng Đông hay Đông Nam của ngôi nhà thì sẽ đem tới nhiều may mắn bởi cây là đại diện cho hành Mộc.', 750000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0002', 1, 'LONI040', 'Cây kim ngân', 'Cây kim ngân ba thân để bàn chậu sứ gấu BearBrick LONI040', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 280000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0003', 1, 'PHIG006', 'Trầu bà đế vương xanh', 'Cây trầu bà đế vương xanh chậu mặt cool ‘Imperial Green’ chậu sứ PHIG006', 'Trầu Bà Đế Vương Xanh (Philodendron Imperial Green) là một giống cây rất ưu chuộng để trồng với ý nghĩa phong thủy mà nó mang lại. Nên đặt cây tại các vị trí như trong phòng khách, phòng làm việc, văn phòng hoặc sảnh ra vào cơ quan.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0004', 1, '', 'Cây cỏ lan chi / cây dây nhện', 'Cây cỏ lan chi để bàn chậu sứ mặt cười SPI004', 'Cỏ Lan Chi (hay còn gọi là Cỏ Nhện) là loại cây rất dễ sống với điều kiện khí hậu nóng tại Việt Nam. Đặc biệt, nó nằm trong top các loại cây lọc không khí rất hiệu quả, có thể đặt trong phòng ngủ giúp bạn có giấc ngủ trong lành hơn.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0005', 1, 'PHIR008', 'Trầu bà đế vương đỏ', 'Cây trầu bà đế vương đỏ để bàn ‘Red Rojo’ chậu sứ PHIR008', 'Thân dạng thảo lớn, trong tự nhiên cây có thể leo được, nhưng đa số khi trồng chậu thì thân vươn thẳng chắc chắn, cao khoảng dưới 1m5.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0006', 1, 'STBS001', 'Cây lưỡi hổ Thái xanh', 'Cây lưỡi hổ Bantel Sensation chậu ươm STBS001', 'Lưỡi hổ Thái xanh mini là dòng lưỡi hổ nhỏ để bàn, có chiều cao tối đa khoảng 20cm, không có nhiều vằn như những loại lưỡi hổ khác, nhưng nó lại sở hữu bộ lá màu xanh đậm ấn tượng, mang lại nét ‘cứng cáp’ tự nhiên cho không gian. Chúng rất thích hợp để trên bàn làm việc, kệ trang trí hoặc làm quà tặng.', 160000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0007', 1, 'RADE033', 'Cây hạnh phúc', 'Cây hạnh phúc để sàn 2 tầng lớn chậu đá mài RADE033', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa mang lại may mắn và hạnh phúc nên cây thường được chọn để làm cây trưng trong nhà hoặc làm quà tặng.', 1200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0008', 1, 'CTBC007', 'Trầu bà xanh', 'Cây trầu bà cột chậu xi măng trụ vuông CTBC007', 'Có tác dụng thanh lọc không khí trong nhà mà còn chứa đựng ý nghĩa phong thủy rất tốt, mang dáng vẻ thanh lịch và sang trọng.', 1100000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0009', 1, 'ANTH010', 'Cây hồng môn', 'Cây hồng môn cỡ nhỏ chậu sứ trắng ANTH010', 'Cây hồng môn được biết tới là một loại cây cảnh mang lại điều tốt lành, có thể giúp điều hòa khí phong thủy trong nhà, có khả năng thu hút những dòng khí tích cực và làm tiêu bớt dòng khí tiêu cực cho môi trường xung quanh, trồng cây hồng môn trong nhà sẽ giúp không gian sống trở nên hài hòa và bình yên hơn.', 240000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0010', 1, 'SCHE020', 'Ngũ gia bì cẩm thạch', 'Cây ngũ gia bì cẩm thạch nhỏ chậu ươm SCHE020', 'Ngũ gia bì là loại cây mang lại tài vận, thịnh vượng và may mắn, trồng trong nhà sẽ mang đến một không gian thư thái, trong lành cho gia đình.', 100000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0011', 1, 'LONI039', 'Cây kim ngân', 'Cây kim ngân một thân để bàn chậu sứ LONI039', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 480000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0012', 1, 'LIVI005', 'Cọ lá xẻ', 'Cây cọ lá xẻ mini để bàn chậu sứ hoa văn LIVI005', 'Cây cọ nhật là loại cây trong nhà có kiểu lá xòe rộng như những cánh quạt, giúp trang trí không gian thêm xanh mát. Nó còn là loại cây phong thủy tượng trưng cho sự giàu sang và tiền tài nên rất đáng trồng trên bàn làm việc.', 160000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0013', 1, 'RADE032', 'Cây hạnh phúc', 'Cây hạnh phúc để sàn 2 tầng chậu sứ hoa văn RADE032', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa mang lại may mắn và hạnh phúc nên cây thường được chọn để làm cây trưng trong nhà hoặc làm quà tặng.', 900000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0014', 1, 'RADE031', 'Cây hạnh phúc', 'Cây hạnh phúc để sàn chậu sứ RADE031', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa mang lại may mắn và hạnh phúc nên cây thường được chọn để làm cây trưng trong nhà hoặc làm quà tặng.', 600000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0015', 1, 'LONI038', 'Cây kim ngân', 'Cây kim ngân để bàn thắt bính tiểu cảnh chậu sứ LONI038', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 380000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0016', 1, 'SHBG005', 'Cây lưỡi hổ Thái xanh', 'Cây lưỡi hổ xanh để bàn mini ‘Black Gold’ chậu sứ SHBG005', 'Lưỡi hổ Thái xanh mini là dòng lưỡi hổ nhỏ để bàn, có chiều cao tối đa khoảng 20cm, không có nhiều vằn như những loại lưỡi hổ khác, nhưng nó lại sở hữu bộ lá màu xanh đậm ấn tượng, mang lại nét ‘cứng cáp’ tự nhiên cho không gian. Chúng rất thích hợp để trên bàn làm việc, kệ trang trí hoặc làm quà tặng.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0017', 1, 'PEAC005', 'Lan ý', 'Cây Lan ý chậu cỡ lớn để bàn chậu sứ trắng PEAC005', 'Những bông hoa trắng tinh khiết vươn lên đầy kiêu hãnh,phần còn lại phải kể đến vẻ đẹp được tạo từ những chiếc lá xanh tươi, tràn đầy sức sống.', 240000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0018', 1, 'AGLA104', 'Cây Phú Quý', 'Cây phú quý chậu sứ thổ cẩm để bàn AGLA104', 'Cây Phú Quý có ý nghĩa mang lại may mắn, phú quý, đại cát đại lợi, bình an cho chủ nhân. Để Trúc Phú Quý mang lại ý nghĩa trọn vẹn bạn lưu ý về vị trí đặt theo hướng hợp với bản mệnh của mình.', 320000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0019', 1, 'PHIG005', 'Trầu bà đế vương xanh', 'Cây trầu bà đế vương xanh ‘Imperial Green’ chậu sứ PHIG005', 'Trầu Bà Đế Vương Xanh (Philodendron Imperial Green) là một giống cây rất ưu chuộng để trồng với ý nghĩa phong thủy mà nó mang lại. Nên đặt cây tại các vị trí như trong phòng khách, phòng làm việc, văn phòng hoặc sảnh ra vào cơ quan.', 320000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0020', 1, 'AGSN010', 'Cây ngọc ngân', 'Cây ngọc ngân để bàn chậu sứ AGSN010', 'Cây ngọc ngân là là cây trong nhà được đánh giá cao về phong thuỷ, người ta quan niệm rằng cây Ngọc ngân mang đến tài lộc, may mắn khi đặt trên bàn làm việc hay trang trí trong văn phòng. Về mặt tinh thần cây Ngọc ngân rất thích hợp làm quà tặng tình yêu.', 160000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0021', 1, 'LONI037', 'Cây kim ngân', 'Cây kim ngân 3 thân để bàn chậu marle LONI037', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 140000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0022', 1, 'BUBU007', 'Bàng Đài Loan cẩm thạch', 'Cây bàng Đài Loan cẩm thạch chậu sứ BUBU007', 'Cây Bàng Đài Loan Cẩm Thạch có lá nhỏ xinh và sắc xanh, viền trắng hồng rất khác biệt và nổi bật, giúp không gian trở nên độc đáo và sang trọng', 1200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0023', 1, 'RADE030', 'Cây hạnh phúc', 'Cây hạnh phúc một thân cổ thụ để bàn chậu sứ RADE030', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa mang lại may mắn và hạnh phúc nên cây thường được chọn để làm cây trưng trong nhà hoặc làm quà tặng.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0024', 1, 'LYRA048', 'Bàng Singapore / Sung tỳ bà', 'Cây bàng Singapore mini để bàn chậu sứ LYRA048', 'Cây Bàng Singapore Lớn có thể dễ dàng nhận ra ở những góc quán cafe, bàn làm việc công. Với những chiếc lá căng bóng hình đàn vĩ cầm rất lớn, nhiều gân là hình chân chim nổi bật.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0025', 1, 'RADE029', 'Cây hạnh phúc', 'Cây hạnh phúc mini để bàn chậu sứ RADE029', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa mang lại may mắn và hạnh phúc nên cây thường được chọn để làm cây trưng trong nhà hoặc làm quà tặng.', 150000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0026', 1, '', 'Cây kim tiền / Kim phát tài', 'Cây kim tiền mini để bàn chậu sứ ZAMI033', 'Cây kim tiền có lá hình xương cá màu xanh mướt. Loài này rất dễ sống phát triển mạnh ở những nơi có độ ẩm cao, thường được trồng để trang trí trong phòng khách, làm quà tặng tân gia, khai trương cửa hàng.', 150000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0027', 1, 'AGRA005', 'Cây hồng phát lộc', 'Cây vạn lộc son ‘Red Anjamani’ để bàn chậu gốm hoa AGRA005', 'Cô tòng đuôi lươn sở hữu bộ lá nhiều màu sắc rực rỡ, cùng những đường viền hoa văn độc đáo, chúngthường được trồng trang trí cho bồn hoa, ban công hoặc trước hiên nhà. Loại cây này đôi lúc được trồng trong nhà, nhưng đòi hỏi phải trồng nơi có nhiều ánh sáng, gần cửa sổ.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0028', 1, 'PEOB006', 'Cây trường sinh', 'Cây trường sinh xanh để bàn chậu sứ PEOB006', 'Cây trường sinh xanh là một loại cây cảnh trong nhà đẹp mắt, với bộ lá luôn xanh mát, sức sống mãnh liệt, dễ chăm sóc. Đây cũng là loại cây phong thủy với tác dụng mang đến sức khỏe, may mắn và tiền tài tới cho gia chủ. Người ta thường hay tặng cây trường sinh vào những dịp đặc biệt như là một lời chúc phúc tới người nhận.', 140000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0029', 1, 'PHGO006', 'Cây trầu bà đế vương vàng', 'Cây trầu bà đế vương vàng chậu sứ họa tiết PHGO006', 'Trầu Bà Đế Vương Vàng có tên tiếng Anh là Philodendron Imperial Golden, là dòng cây thân thảo, có sức sống bền bỉ và rất dễ chăm sóc. Loài cây này có phần lá non màu vàng tươi tại trung tâm, khi già đi thì lá chuyển sang màu xanh trong tự nhiên rất bắt mắt. Đây là một loại cây trang trí nội thất rất tuyệt vời, nó giúp không gian sống có thêm điểm nhấn bởi phần lá màu vàng của mình.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0030', 1, 'AGRA004', 'Cây hồng phát lộc', 'Cây vạn lộc son ‘Red Anjamani’ để bàn chậu sứ AGRA004', 'Cô tòng đuôi lươn sở hữu bộ lá nhiều màu sắc rực rỡ, cùng những đường viền hoa văn độc đáo, chúngthường được trồng trang trí cho bồn hoa, ban công hoặc trước hiên nhà. Loại cây này đôi lúc được trồng trong nhà, nhưng đòi hỏi phải trồng nơi có nhiều ánh sáng, gần cửa sổ.', 220000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0031', 1, 'LONI036', 'Cây kim ngân', 'Cây kim ngân nơ để bàn chậu sứ trắng LONI036', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 150000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0032', 1, 'SAFS005', 'Cây lưỡi hổ thái', 'Cây lưỡi hổ Thái để bàn ‘Futura superba’ chậu sứ SAFS005', 'Lưỡi hổ là loại cây cảnh nội thất kinh điển nhất, bởi sức sống bền bỉ không cần tốn công chăm sóc, lại còn được NASA đánh giá là có khả năng thanh lọc không khí rất hiệu quả bằng một cơ chế hấp thu các loại chất khí độc hại trên bộ lá.', 360000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0033', 1, 'TBTX009-1', 'Cây trầu bà thanh xuân / Cây trầu bà tay Phật', 'Cây trầu bà thanh xuân chậu sứ họa tiết TBTX010', 'Trầu bà thanh xuân là loại cây trong nhà độc đáo được mọi người ưa thích và trưng bày trang trí. Trầu Bà Thanh Xuân có ý nghĩa về mặt phong thủy nó còn có ý nghĩa may mắn, đem lại tài lộc, gia tăng vận khí, thể hiện ý chí vươn lên của gia chủ.', 950000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0034', 1, 'SCHE019', 'Ngũ gia bì', 'Cây ngũ gia bì để bàn chậu sứ thổ cẩm SCHE019', 'Ngũ gia bì là loại cây mang lại tài vận, thịnh vượng và may mắn, trồng trong nhà sẽ mang đến một không gian thư thái, trong lành cho gia đình.', 220000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0035', 1, 'CPTN014', 'Phát tài núi', 'Cây phát tài núi 2 tầng dáng thấp chậu đá mài CPTN014', 'Cây phát tài núi rất thường được lựa chọn để làm quà tặng vào những dịp khai trương, lên nhà mới, văn phòng mới… với mong muốn đem lại nhiều tài lộc và may mắn cho người được tặng.', 1650000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0036', 1, 'TUBO004', 'Cây tùng bồng lai', 'Cây tùng bồng lai bonsai tiểu cảnh chậu sứ TUBO004', 'Cây Tùng Bông Lai là một loài cây mang vẻ đẹp trang nhã, với tán lá dầy xanh mướt trông giống như những đám mây và có kích thước nhỏ gọn nên rất thích hợp để bàn làm việc. Chúng là loài cây dễ trồng nên không cần phải tốn công chăm sóc, tuy nhiên cần đặt tại nơi có nhiều ánh sáng.', 2200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0037', 1, 'PHIR007', 'Trầu bà đế vương đỏ', 'Cây trầu bà đế vương đỏ ‘Red Rojo’ chậu sứ PHIR007', 'Thân dạng thảo lớn, trong tự nhiên cây có thể leo được, nhưng đa số khi trồng chậu thì thân vươn thẳng chắc chắn, cao khoảng dưới 1m5.', 550000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0038', 1, 'LONI035', 'Cây kim ngân', 'Cây kim ngân thắt bính tiểu cảnh chậu đất nung LONI035', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 420000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0039', 1, 'HEDE002', 'Cây thường xuân cẩm thạch', 'Cây thường xuân cẩm thạch chậu nhựa HEDE002', 'Thường xuân là loài cây dây leo, chúng có thể sống trong nhà rất tốt và sinh trưởng khỏe mạnh vào mùa mát mẻ. Những cành dài buông rủ của chúng là gợi ý tuyệt vời trên bục cửa sổ, trên kệ sách hoặc treo lên cao. Chúng có sức sống khá bền bỉ nên điều kiện chăm sóc cũng không quá phức tạp.', 160000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0040', 1, 'PHBI005', 'Cây hồng hạc chân cam', 'Cây hồng hạc thân cam “Billietiae’ chậu banh PHBI005', 'Cây Hạc Cam đặc trưng bởi những chiếc lá thuôn dài, hình dây đeo có xẻ trái tim sâu, phần cuống cũng được tô điểm bởi màu da cam rất bắt mắt. Khi trưởng thành cây hạc Philodendron billietiae có kích thước rất khủng nên được nhiều người săn đón sưu tầm.', 550000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0041', 1, 'LYRA047', 'Bàng Singapore / Sung tỳ bà', 'Cây bàng Singapore 2 thân chậu đá mài LYRA047', 'Cây Bàng Singapore Lớn có thể dễ dàng nhận ra ở những góc quán cafe, bàn làm việc công. Với những chiếc lá căng bóng hình đàn vĩ cầm rất lớn, nhiều gân là hình chân chim nổi bật.', 680000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0042', 1, 'LYRA046', 'Bàng Singapore / Sung tỳ bà', 'Cây bàng Singapore nhiều thân chậu sứ trắng LYRA046', 'Cây Bàng Singapore Lớn có thể dễ dàng nhận ra ở những góc quán cafe, bàn làm việc công. Với những chiếc lá căng bóng hình đàn vĩ cầm rất lớn, nhiều gân là hình chân chim nổi bật.', 2250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0043', 1, 'BDCT006', 'Đa búp đỏ cẩm thạch', 'Cây đa búp đỏ cẩm thạch 1 thân lớn chậu sứ BDCT006', 'Từ lâu cây Đa luôn là biểu tượng của sự trường tồn, sức sống dẻo dai, nơi chứa đựng thần quyền và tâm linh của con người. Chính lẽ đó mà trong phong thủy, Đa Búp Đỏ cũng mang ý nghĩa của sự may mắn, an lành.', 570000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0044', 1, 'SCHE018', 'Ngũ gia bì', 'Cây ngũ gia bì để bàn chậu đất nung SCHE018', 'Ngũ gia bì là loại cây mang lại tài vận, thịnh vượng và may mắn, trồng trong nhà sẽ mang đến một không gian thư thái, trong lành cho gia đình.', 150000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0045', 1, 'DRCO004', 'Trúc mặt trời / Phát tài Nhật', 'Cây trúc mặt trời ‘Compacta’ chậu sứ họa tiết thổ DRCO004', 'Cây phát tài Nhật tượng trưng cho sức sống bền bỉ, mạnh mẽ. Cây có thể thích nghi với mọi điều kiện thời tiết, tán lá vẫn xanh vẫn vươn thêm nhiều tầng mới. Vì thế mà cây mang lại nguồn sinh khí dồi dào, một tinh thần tích cực, cùng niềm vui và sự may mắn cho mọi người.', 200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0046', 1, 'ABDS004', 'Môn vảy rồng', 'Cây môn vảy rồng ‘Dragon Scale’ chậu sứ trắng ABDS004', 'Bạn không cần dành quá nhiều thời gian cho Rồng Bạc nhưng vẫn ngắm nhìn được vẻ đẹp của nó mỗi ngày.', 320000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0047', 1, 'PEOB005', 'Cây trường sinh', 'Cây trường sinh để bàn chậu đất nung Marble PEOB005', 'Cây trường sinh (Peperomia obtusifolia) là một loại cây cảnh trong nhà đẹp mắt, với bộ lá luôn xanh mát, sức sống mãnh liệt, dễ chăm sóc. Đây cũng là loại cây phong thủy với tác dụng mang đến sức khỏe, may mắn và tiền tài tới cho gia chủ. Người ta thường hay tặng cây trường sinh vào những dịp đặc biệt như là một lời chúc phúc tới người nhận.', 200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0048', 1, 'LONI034', 'Cây kim ngân', 'Cây kim ngân thắt bính nhỏ để bàn chậu sứ LONI034', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 140000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0049', 1, 'RADE028', 'Cây hạnh phúc', 'Cây hạnh phúc để sàn chậu sứ RADE028', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa mang lại may mắn và hạnh phúc nên cây thường được chọn để làm cây trưng trong nhà hoặc làm quà tặng.', 560000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0050', 1, 'KIGI002', 'Cây kim giao', 'Cây kim giao để bàn chậu sứ trắng KIGI002', 'Kim giao là một cây thân gỗ có giá trị kinh tế lớn. Nhờ có sức sống mạnh mẽ, kiểu dáng đẹp, lá xum xuê và xanh mướt nên chúng cũng được nhân giống để làm cây cảnh để bàn, giúp tạo không gian xanh mát và thư giãn. Đồng thời đây cũng là một loại cây mang ý nghĩa phong thủy rất tốt. Rất thích hợp làm quà tặng.', 240000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0051', 1, 'DRDO003-1', 'Cây phát tài xanh', 'Cây phát tài xanh lá lớn ‘Green Jewel’ chậu ươm DRGJ001', 'Phát tài xanh là loại cây trồng trong nhà dễ chăm sóc, với sức sống bền bỉ và khả năng chịu râm mát cực kì tốt. Loại cây này có bộ lá màu xanh đậm, dáng cuộn tròn tỏa ra xung quanh, giúp mang lại màu xanh cho không gian sống. Đồng thời đây là loại cây có khả năng thanh lọc không khí rất hiệu quả.', 560000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0052', 1, 'LONI033', 'Cây kim ngân', 'Cây kim ngân ‘Tay Phật’ để bàn chậu sứ LONI033', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0053', 1, 'RADE027', 'Cây hạnh phúc', 'Cây hạnh phúc để bàn chậu sứ trụ xanh RADE027', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa mang lại may mắn và hạnh phúc nên cây thường được chọn để làm cây trưng trong nhà hoặc làm quà tặng.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0054', 1, 'LONI032', 'Cây kim ngân', 'Cây kim ngân một thân để bàn chậu sứ LONI032', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 270000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0055', 1, 'STGF005', 'Cây lưỡi hổ hoàng kim', 'Cây lưỡi hổ Hoàng Kim ‘Gold Flame’ chậu sứ STGF005', 'Lưỡi hổ Gold Flame là dòng “cây cảnh quốc dân” được nhiều người lựa chọn để trồng trang trí trong nhà. Nó không những dễ sống đến mức “gần như bất tử” mà còn giúp mang lại vẻ đẹp tự nhiên, trên lá có những sọc vằn ấn tượng, lá cứng cáp và hình tượng rõ ràng, phù hợp để trồng trong mọi không gian nội thất. Bện cạnh đó, cây lưỡi hổ còn được xem là dòng cây phong thủy mang lại nhiều ý nghĩa cho gia chủ.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0056', 1, 'PHIG004', 'Trầu bà đế vương xanh', 'Cây trầu bà đế vương xanh ‘Imperial Green’ chậu sứ PHIG004', 'Trầu Bà Đế Vương Xanh (Philodendron Imperial Green) là một giống cây rất ưu chuộng để trồng với ý nghĩa phong thủy mà nó mang lại. Nên đặt cây tại các vị trí như trong phòng khách, phòng làm việc, văn phòng hoặc sảnh ra vào cơ quan.', 220000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0057', 1, 'PHBR006-1', 'Trầu bà đế vương kim cương', 'Cây trầu bà đế vương kim cương ‘Birkin’ chậu sứ PHBR007', 'Đế vương kim cương sở hữu bộ lá xanh khoẻ khoắn, xen kẽ vân sọc trắng hài hoà và đầy cuốn hút. Chắc chắn nó sẽ là điểm nhấm tuyệt vời cho không gian nội thất, bớt đi sự nhàm chán và giúp cải thiện chất lượng không khí hiệu quả.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0058', 1, 'DRDO003', 'Cây phát tài viền vàng', 'Cây phát tài viền vàng ‘Dorado’ thủy sinh DRDO003', 'Phát tài viền vàng là loại cây trồng trong nhà dễ chăm sóc, với sức sống bền bỉ và khả năng chịu râm mát cực kì tốt. Loại cây này có bộ lá màu xanh đậm, viền lá màu vàng và dáng cuộn tròn tỏa ra xung quanh, giúp mang lại màu xanh cho không gian sống. Đồng thời đây là loại cây có khả năng thanh lọc không khí rất hiệu quả.', 220000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0059', 1, 'LONI031', 'Cây kim ngân', 'Cây kim ngân ‘phát tài phát lộc’ để bàn chậu sứ LONI031', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 340000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0060', 1, 'LICU010', 'Cọ Nhật', 'Cây cọ Nhật nhỏ chậu sứ trắng LICU010', 'Cây cọ nhật là loại cây trong nhà có kiểu lá xòe rộng như những cánh quạt, giúp trang trí không gian thêm xanh mát. Nó còn là loại cây phong thủy tượng trưng cho sự giàu sang và tiền tài nên rất đáng trồng trên bàn làm việc.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0061', 1, 'LUCK002', 'Cỏ may mắn', 'Cây may mắn hình trái tim chậu sứ LUCK002', 'Cây cỏ may mắn là một loại cây đặc biệt, được ươm từ những hạt thanh long và tạo hình bởi bàn tay khéo léo của người nghệ nhân. Trên những quả cầu màu xanh xanh là hàng vạn cây non được ươm trổ trông rất bắt mắt và độc đáo. Chúng được xem là biểu tượng cho tình yêu, hy vọng và sự may mắn nên thường được lựa chọn để làm quà tặng vào dịp đặc biệt.', 240000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0062', 1, 'LUCK001', 'Cỏ may mắn', 'Cây may mắn dáng tree chậu sứ chữ nhật LUCK001', 'Cây cỏ may mắn là một loại cây đặc biệt, được ươm từ những hạt thanh long và tạo hình bởi bàn tay khéo léo của người nghệ nhân. Trên những quả cầu màu xanh xanh là hàng vạn cây non được ươm trổ trông rất bắt mắt và độc đáo. Chúng được xem là biểu tượng cho tình yêu, hy vọng và sự may mắn nên thường được lựa chọn để làm quà tặng vào dịp đặc biệt.', 280000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0063', 1, 'CHAM005-2', 'Cau tiểu trâm', 'Cây cau tiểu trâm mini mèo đi học chậu đất nung CHAM006', 'Công việc đòi hỏi bạn phải tiếp xúc hằng ngày với các thiết bị hiện đại có nhiều bức xạ điện tử như máy tính, máy in, điện thoại khiến cho tình trạng sức khỏe bị ảnh hưởng, dẫn đến giảm chất lượng công việc. Hãy thử trồng một cây Cau Tiểu Trâm để bàn với tác dụng tuyệt vời đã được minh chứng.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0064', 1, 'LIVI003', 'Cọ lá xẻ / Kè ta', 'Cây cọ lá lẻ mini ‘Livistona’ chậu sứ LIVI003', '', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0065', 1, 'PHXA004', 'Cây Trầu Bà Chân Vịt', 'Cây trầu bà thiên nga để bàn chậu sứ PHXA004', 'Trầu Bà Chân Vịt có thể còn lạ lẫm với nhiều người, không biết hình dạng của loài cây đó ra sao. Đó là một loài cây có đặc tính sinh trưởng mạnh mẽ, thường được mọi người dùng để tô điểm cho không gian sinh sống, trang trí nội thất.', 420000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0066', 1, 'STGH003', 'Cây lưỡi hổ Thái vàng mini', 'Cây lưỡi hổ vàng mini ‘Golden Hahnii’ chậu sứ STGH003', 'Lưỡi hổ Thái vàng “Golden Hahnii” là dòng cây cảnh mini với những chiếc lá nhỏ nhắn có màu vàng sáng sọc xanh đẹp mắt, rất phù hợp để trồng trên bàn làm việc, kệ sách hoặc trang trí tiểu cảnh. Cây lưỡi hổ cũng được trồng với tác dụng phong thủy như là sự may mắn cho gia chủ.', 220000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0067', 1, 'ANTH009', 'Cây hồng môn', 'Cây hồng môn cỡ nhỏ chậu đất nung ANTH009', 'Cây hồng môn được biết tới là một loại cây cảnh mang lại điều tốt lành, có thể giúp điều hòa khí phong thủy trong nhà, có khả năng thu hút những dòng khí tích cực và làm tiêu bớt dòng khí tiêu cực cho môi trường xung quanh, trồng cây hồng môn trong nhà sẽ giúp không gian sống trở nên hài hòa và bình yên hơn.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0068', 1, 'LONI030', 'Cây kim ngân bính', 'Cây kim ngân để bàn một thân chậu sứ LONI030', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 360000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0069', 1, 'PERE004', 'Cây trường sinh cẩm thạch', 'Cây trường sinh viền đỏ chậu sứ hoa văn PERE004', 'Cây trường sinh lá đỏ là một loại cây cảnh trong nhà đẹp mắt, với bộ lá luôn xanh mát, sức sống mãnh liệt, dễ chăm sóc. Đây cũng là loại cây phong thủy với tác dụng mang đến sức khỏe, may mắn và tiền tài tới cho gia chủ. Người ta thường hay tặng cây trường sinh vào những dịp đặc biệt như là một lời chúc phúc tới người nhận.', 240000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0070', 1, 'LONI029', 'Cây kim ngân bính', 'Cây kim ngân để bàn một thân chậu sứ LONI029', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 300000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0071', 1, 'PHBR007', 'Trầu bà đế vương kim cương', 'Cây trầu bà đế vương kim cương ‘Birkin’ chậu sứ PHBR007', 'Đế vương kim cương sở hữu bộ lá xanh khoẻ khoắn, xen kẽ vân sọc trắng hài hoà và đầy cuốn hút. Chắc chắn nó sẽ là điểm nhấm tuyệt vời cho không gian nội thất, bớt đi sự nhàm chán và giúp cải thiện chất lượng không khí hiệu quả.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0072', 1, 'AGLE004', 'Cây cung điện vàng', 'Cây cung điện vàng ‘Pink Legacy’ chậu sứ AGLE004', 'Cây cung điện sở hữu bộ lá nhiều màu sắc rực rỡ như chốn “hoàng cung”, cùng với đó là những đường viền hoa văn độc đáo như tranh vẽ. Loại cây này rất dễ chăm sóc, thích hợp để trang trí không gian phòng khách, quầy tiếp tân, sảnh ra vào hoặc để bàn làm việc.', 280000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0073', 1, 'SHBG004', 'Cây lưỡi hổ Thái xanh', 'Cây lưỡi hổ xanh mini ‘Black Gold’ chậu sứ SHBG004', 'Lưỡi hổ Thái xanh mini là dòng lưỡi hổ nhỏ để bàn, có chiều cao tối đa khoảng 20cm, không có nhiều vằn như những loại lưỡi hổ khác, nhưng nó lại sở hữu bộ lá màu xanh đậm ấn tượng, mang lại nét ‘cứng cáp’ tự nhiên cho không gian. Chúng rất thích hợp để trên bàn làm việc, kệ trang trí hoặc làm quà tặng.', 150000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0074', 1, 'BDCT005', 'Đa búp đỏ cẩm thạch', 'Cây đa búp đỏ cẩm thạch 3 thân chậu sứ BDCT005', 'Từ lâu cây Đa luôn là biểu tượng của sự trường tồn, sức sống dẻo dai, nơi chứa đựng thần quyền và tâm linh của con người. Chính lẽ đó mà trong phong thủy, Đa Búp Đỏ cũng mang ý nghĩa của sự may mắn, an lành.', 340000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0075', 1, 'SAFS004', 'Cây lưỡi hổ thái', 'Cây lưỡi hổ Thái để bàn ‘Futura superba’ chậu đất nung SAFS004', 'Lưỡi hổ là loại cây cảnh nội thất kinh điển nhất, bởi sức sống bền bỉ không cần tốn công chăm sóc, lại còn được NASA đánh giá là có khả năng thanh lọc không khí rất hiệu quả bằng một cơ chế hấp thu các loại chất khí độc hại trên bộ lá.', 330000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0076', 1, 'SALA004', 'Lưỡi hổ vàng', 'Cây lưỡi hổ vàng dài ‘Laurentii’ chậu sứ trắng SALA004', 'Đây sẽ là món quà ý nghĩa để tặng đối tác, bạn bè, người thân vào những dịp đặc biệt như mừng sinh nhật, tân gia, mừng năm mới.', 650000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0077', 1, 'CPTN013', 'Phát tài núi', 'Cây phát tài núi 2 tầng chậu đá mài CPTN013', 'Cây phát tài núi rất thường được lựa chọn để làm quà tặng vào những dịp khai trương, lên nhà mới, văn phòng mới… với mong muốn đem lại nhiều tài lộc và may mắn cho người được tặng.', 1900000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0078', 1, 'CVNB001', 'Cau vàng Nhật Bản', 'Cây cau vàng Nhật Bản chậu ươm CVNB001', 'Cau vàng Nhật Bản là dòng cau cảnh chịu bóng râm rất tốt, có chiều cao chỉ từ 80~100cm, tán lá gọn gàng nên rất thích hợp để trưng bày trong không gian nội thất. Là một trong những loại cau kiểng được ưu chuộng bởi khả năng mang lại không gian xanh mát, lọc không khí và ý nghĩa tốt trong phong thủy.', 400000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0079', 1, 'SPSE004', 'Lan ý Mỹ', 'Cây lan ý Mỹ ‘Sensation’ chậu đất nung SPSE004', 'Khác với Lan Ý thông thường, Lan Ý Mỹ có những chiếc lá lớn hơn và đường gân màu xanh nổi bật. Chúng là loài có sức sống cực kì mãnh liệt, chịu hạn tốt và có thể phát triển khỏe mạnh trong môi trường thiếu sáng. Loài cây to lớn này sẽ giúp tạo điểm nhấn tuyệt vời, và đồng thời mang lại không gian xanh mát cho nội thất rất hiệu quả.', 240000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0080', 1, 'PHBR006', 'Trầu bà đế vương kim cương', 'Cây trầu bà đế vương kim cương ‘Birkin’ chậu đất nung PHBR006', 'Đế vương kim cương sở hữu bộ lá xanh khoẻ khoắn, xen kẽ vân sọc trắng hài hoà và đầy cuốn hút. Chắc chắn nó sẽ là điểm nhấm tuyệt vời cho không gian nội thất, bớt đi sự nhàm chán và giúp cải thiện chất lượng không khí hiệu quả.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0081', 1, 'FITR006', 'Đa tam phúc', 'Cây đa tam phúc ‘Triangularis’ chậu đất nung FITR006', 'Đây sẽ là một dạng năng lượng tích cực, bạn sẽ cảm nhận được nhiều giá trị như sức khỏe, sự giàu có hay mối quan hệ tốt đẹp đến với cuộc sống của mình.', 300000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0082', 1, 'SHBG003', 'Cây lưỡi hổ Thái xanh', 'Cây lưỡi hổ xanh mini ‘Black Gold’ chậu sứ SHBG003', 'Lưỡi hổ Thái xanh mini là dòng lưỡi hổ nhỏ để bàn, có chiều cao tối đa khoảng 20cm, không có nhiều vằn như những loại lưỡi hổ khác, nhưng nó lại sở hữu bộ lá màu xanh đậm ấn tượng, mang lại nét ‘cứng cáp’ tự nhiên cho không gian. Chúng rất thích hợp để trên bàn làm việc, kệ trang trí hoặc làm quà tặng.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0083', 1, 'TBTX009', 'Cây trầu bà thanh xuân / Cây trầu bà tay Phật', 'Cây trầu bà thanh xuân chậu đất nung TBTX009', 'Trầu bà thanh xuân là loại cây trong nhà độc đáo được mọi người ưa thích và trưng bày trang trí. Trầu Bà Thanh Xuân có ý nghĩa về mặt phong thủy nó còn có ý nghĩa may mắn, đem lại tài lộc, gia tăng vận khí, thể hiện ý chí vươn lên của gia chủ.', 950000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0084', 1, '', 'Trầu bà xanh', 'Cây trầu bà cột chậu đá mài trụ vuông CTBC006', 'Có tác dụng thanh lọc không khí trong nhà mà còn chứa đựng ý nghĩa phong thủy rất tốt, mang dáng vẻ thanh lịch và sang trọng.', 1250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0085', 1, '', 'Phát tài núi', 'Cây phát tài núi 2 tầng chậu sứ CPTN012', 'Cây phát tài núi rất thường được lựa chọn để làm quà tặng vào những dịp khai trương, lên nhà mới, văn phòng mới… với mong muốn đem lại nhiều tài lộc và may mắn cho người được tặng.', 1900000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0086', 1, 'RADE034', 'Cây hạnh phúc', 'Cây hạnh phúc 1 thân để bàn trang trí tiểu cảnh RADE034', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa mang lại may mắn và hạnh phúc nên cây thường được chọn để làm cây trưng trong nhà hoặc làm quà tặng.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
