('CCNPLT0108', 1, 'CANE004', 'Cây đuôi công ‘Network’', 'Cây đuôi công ‘Network’ chậu sứ trắng CANE004', 'Với những vân lá tạo nên hoạ tiết ô lưới ngẫu nhiên độc đáo như một bức tranh mosaic kì công. Calathea Network tạo nên sự khác biệt không thể tìm thấy với bất kỳ loại cây nào.', 270000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0109', 1, 'NTMX021', 'Cây Đại phú gia / Trầu bà neon', 'Chậu cây nội thất mix Đại phú gia, Trầu bà Neon NTMX021', 'Chậu cây nội thất được mix các loại cây dễ chăm sóc, thích nghi tốt với điều kiện thiếu sáng trong nhà. Sản phẩm được mix các loại cây Đại phú gia, Trầu bà Neon thích hợp đặt tại phòng khách, lối hành lang, văn phòng, sảnh nhà hàng, khách sạn hoặc làm quà tặng.', 820000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0110', 1, 'CASA003', 'Cây đuôi công sọc', 'Cây đuôi công sọc Calathea Sanderiana chậu gốm sứ CASA003', 'Calathea ornata Sanderiana là loài thực vật thân thảo, có lá hình thuôn tròn, sọc trắng xanh. Loài này rất dễ sống, thường được trồng để trang trí trong nhà, thanh lọc không khí và mang ý nghĩa đem lại may mắn, thành công và thịnh vượng.', 230000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0111', 1, 'AGPL003', 'Cây hồng phát lộc', 'Cây hồng phát lộc ‘Pink Lady’ chậu sứ trắng AGPL003', 'Cây hồng phát lộc là loại cây nhỏ để bàn, có màu hồng phớt tươi mới và bắt mắt, giúp tạo điểm nhấn cho góc làm việc thêm sinh động. Chúng còn là dòng cây phong thủy, được cho rằng giúp mang lại những điều may mắn và thịnh vượng tới cho gia chủ. Hồng phát lộc cũng là một món quà rất ý nghĩa vào những dịp khai trương, sinh nhật hoặc dịp lễ.', 240000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0112', 1, '', 'Phát tài núi', 'Cây phát tài núi 2 tầng chậu xi măng họa siết sọc CPTN010', 'Cây phát tài núi rất thường được lựa chọn để làm quà tặng vào những dịp khai trương, lên nhà mới, văn phòng mới… với mong muốn đem lại nhiều tài lộc và may mắn cho người được tặng.', 1850000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0113', 1, 'ABSD004', 'Rồng bạc', 'Cây rồng bạc ‘Silver Dragon’ chậu sứ trắng ABSD004', 'Bạn không cần dành quá nhiều thời gian cho Rồng Bạc nhưng vẫn ngắm nhìn được vẻ đẹp của nó mỗi ngày.', 340000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0114', 1, 'LONI022', 'Cây kim ngân bính', 'Cây kim ngân một thân để bàn chậu sứ LONI022', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0115', 1, 'PHIR006', 'Trầu bà đế vương đỏ', 'Cây trầu bà đế vương đỏ chậu đất nung PHIR006', 'Thân dạng thảo lớn, trong tự nhiên cây có thể leo được, nhưng đa số khi trồng chậu thì thân vươn thẳng chắc chắn, cao khoảng dưới 1m5.', 300000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0116', 1, '', 'Bàng Đài Loan cẩm thạch', 'Cây bàng Đài Loan cẩm thạch chậu xi măng trụ tròn cao BUBU005', 'Cây Bàng Đài Loan Cẩm Thạch có lá nhỏ xinh và sắc xanh, viền trắng hồng rất khác biệt và nổi bật, giúp không gian trở nên độc đáo và sang trọng', 750000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0117', 1, 'PHBR005', 'Trầu bà đế vương kim cương', 'Cây trầu bà đế vương kim cương ‘Birkin’ chậu đất nung PHBR005', 'Đế vương kim cương sở hữu bộ lá xanh khoẻ khoắn, xen kẽ vân sọc trắng hài hoà và đầy cuốn hút. Chắc chắn nó sẽ là điểm nhấm tuyệt vời cho không gian nội thất, bớt đi sự nhàm chán và giúp cải thiện chất lượng không khí hiệu quả.', 420000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0118', 1, 'HCMX002', 'Hoa cúc mâm xôi', 'Chậu hoa cúc mâm xôi trang trí xọt nhựa HCMX002', 'Cúc mâm xôi là một trong các loài hoa không thể thiếu vào dịp Tết cổ truyền. Chậu cúc mâm xôi có hình dáng tỏa ra theo dạng hình cầu, mang ý nghĩa vẹn tròn, cho một năm mới xuôn sẻ. Với màu vàng được xem là màu của sung túc, đầm ấm và nhiều tài lộc hơn. Do đó, người ta thường tặng cả cặp làm quà tặng cho ông bà, cha mẹ hoặc quý đối tác.', 160000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0119', 1, 'CDPG006', 'Đại phú gia', 'Cây đại phú gia chậu xi măng đá mài CDPG006', 'Cây đại phú gia để bàn thuộc họ cây ráy, có tên khoa học là Aglaoocma SP. Loài cây này mọc phân bổ chủ yếu ở các nước có khí hậu nhiệt đới, ưa ẩm và bóng râm có nguồn gốc từ châu Mỹ.', 840000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05');
INSERT INTO `products` (`product_id`, `type`, `code`, `name`, `short_description`, `detailed_description`, `price`, `total_orders`, `total_ratings`, `overall_stars`, `is_returnable`, `created_at`, `updated_at`) VALUES
('CCNPLT0120', 1, 'BDCT004', 'Đa búp đỏ cẩm thạch', 'Cây đa búp đỏ cẩm thạch một thân chậu sứ BDCT004', 'Từ lâu cây Đa luôn là biểu tượng của sự trường tồn, sức sống dẻo dai, nơi chứa đựng thần quyền và tâm linh của con người. Chính lẽ đó mà trong phong thủy, Đa Búp Đỏ cũng mang ý nghĩa của sự may mắn, an lành.', 300000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0121', 1, 'AGRA003', 'Cây hồng phát lộc', 'Cây vạn lộc son ‘Red Anjamani’ để bàn chậu sứ AGRA003', 'Cô tòng đuôi lươn sở hữu bộ lá nhiều màu sắc rực rỡ, cùng những đường viền hoa văn độc đáo, chúngthường được trồng trang trí cho bồn hoa, ban công hoặc trước hiên nhà. Loại cây này đôi lúc được trồng trong nhà, nhưng đòi hỏi phải trồng nơi có nhiều ánh sáng, gần cửa sổ.', 320000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0122', 1, 'TUBO003', 'Cây tùng bồng lai', 'Cây tùng bồng lai cặp đôi tình yêu chậu sứ TUBO003', 'Cây Tùng Bông Lai là một loài cây mang vẻ đẹp trang nhã, với tán lá dầy xanh mướt trông giống như những đám mây và có kích thước nhỏ gọn nên rất thích hợp để bàn làm việc. Chúng là loài cây dễ trồng nên không cần phải tốn công chăm sóc, tuy nhiên cần đặt tại nơi có nhiều ánh sáng.', 320000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0123', 1, '', 'Cây tùng bồng lai', 'Cây Tùng bồng lai chậu đất nung wax hoa cúc TUBO002', 'Cây Tùng Bông Lai là một loài cây mang vẻ đẹp trang nhã, với tán lá dầy xanh mướt trông giống như những đám mây và có kích thước nhỏ gọn nên rất thích hợp để bàn làm việc. Chúng là loài cây dễ trồng nên không cần phải tốn công chăm sóc, tuy nhiên cần đặt tại nơi có nhiều ánh sáng.', 320000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0124', 1, '', 'Cây hạnh phúc', 'Cây hạnh phúc chậu đất nung hoàng gia RADE023', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa mang lại may mắn và hạnh phúc nên cây thường được chọn để làm cây trưng trong nhà hoặc làm quà tặng.', 750000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0125', 1, '', 'Cây cỏ lan chi / cây dây nhện', 'Cây cỏ lan chi để bàn chậu men bóng họa tiết SPI003', 'Cỏ Lan Chi (hay còn gọi là Cỏ Nhện) là loại cây rất dễ sống với điều kiện khí hậu nóng tại Việt Nam. Đặc biệt, nó nằm trong top các loại cây lọc không khí rất hiệu quả, có thể đặt trong phòng ngủ giúp bạn có giấc ngủ trong lành hơn.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0126', 1, 'LYRA041', 'Cây bàng Singapore', 'Cây bàng Singapore một thân chậu đất nung LYRA041', 'Cây Bàng Singapore là loại cây nội thất đang được ưa chuộng nhất hiện nay. Cây có khả năng cải thiện chất lượng không khí. Mang lại sự xanh mát và sang trọng cho không gian của bạn, trang trí phòng khách, văn phòng, quán cafe hay shop.Hoặc làm quà tặng dịp khai trương, tân gia…', 340000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0127', 1, '', 'Cây Trầu Bà Chân Vịt', 'Cây Trầu Bà Chân Vịt chậu sứ trắng có vân Philodendron Xanadu CTBCV002', 'Khi nói đến Trầu Bà Chân Vịt thì có thể nhiều người sẽ cảm thấy lạ lẫm kèm theo đó là tò mò không biết hình dạng của loài cây đó ra sao. Đó là một loài cây có đặc tính sinh trưởng mạnh mẽ, thường được mọi người dùng để tô điểm cho không gian sinh sống, trang trí nội thất.', 750000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0128', 1, 'ZAMI031', 'Cây kim tiền / Kim phát tài', 'Cây kim tiền trưng tết chậu đất nung hoa cúc ZAMI031', 'Cây kim tiền có lá hình xương cá màu xanh mướt. Loài này rất dễ sống phát triển mạnh ở những nơi có độ ẩm cao, thường được trồng để trang trí trong phòng khách, làm quà tặng tân gia, khai trương cửa hàng.', 420000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0129', 1, '', 'Cây ngọc ngân', 'Cây ngọc ngân Thái chậu gốm họa tiết cầu vồng AGSN009', 'Cây ngọc ngân là là cây trong nhà được đánh giá cao về phong thuỷ, người ta quan niệm rằng cây Ngọc ngân mang đến tài lộc, may mắn khi đặt trên bàn làm việc hay trang trí trong văn phòng. Về mặt tinh thần cây Ngọc ngân rất thích hợp làm quà tặng tình yêu.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0130', 1, '', 'Cây kim ngân bính', 'Cây kim ngân bính để bàn chậu sứ có hình LONI021', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0131', 1, 'RADE026', 'Cây hạnh phúc', 'Cây hạnh phúc bonsai để bàn chậu sứ RADE026', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa mang lại may mắn và hạnh phúc nên cây thường được chọn để làm cây trưng trong nhà hoặc làm quà tặng.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0132', 1, 'ANTH008', 'Cây hồng môn', 'Cây hồng môn tiểu cảnh thỏ trắng ôm túi may mắn ANTH008', 'Cây hồng môn được biết tới là một loại cây cảnh mang lại điều tốt lành, có thể giúp điều hòa khí phong thủy trong nhà, có khả năng thu hút những dòng khí tích cực và làm tiêu bớt dòng khí tiêu cực cho môi trường xung quanh, trồng cây hồng môn trong nhà sẽ giúp không gian sống trở nên hài hòa và bình yên hơn.', 450000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0133', 1, 'HCMX001', 'Hoa cúc mâm xôi', 'Chậu hoa cúc mâm xôi trưng Tết cỡ lớn trang trí xọt tre HCMX001', 'Cúc mâm xôi là một trong các loài hoa không thể thiếu vào dịp Tết cổ truyền. Chậu cúc mâm xôi có hình dáng tỏa ra theo dạng hình cầu, mang ý nghĩa vẹn tròn, cho một năm mới xuôn sẻ. Với màu vàng được xem là màu của sung túc, đầm ấm và nhiều tài lộc hơn. Do đó, người ta thường tặng cả cặp làm quà tặng cho ông bà, cha mẹ hoặc quý đối tác.', 380000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0134', 1, 'CTBH008', 'Trúc bách hợp', 'Cây trúc bách hợp một thân chậu xi măng CTBH008', 'Còn được gọi là trúc phất dụ, mọc thành bụi sum sê, ở chính giữa tạo thành hình hoa thị và tua tủa ra xung quanh. Cây có thân cứng, lá thuôn nhọn ở đầu, mép nguyên, màu xanh bóng xen lần dải màu vàng tươi từ gốc tới ngọn', 950000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0135', 1, 'LONI020', 'Cây kim ngân', 'Cây kim ngân thắt bính chậu đất nung LONI020', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 990000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0136', 1, 'ANTH007', 'Cây hồng môn', 'Cây hồng môn cỡ lớn chậu gốm sứ ANTH007', 'Cây hồng môn được biết tới là một loại cây cảnh mang lại điều tốt lành, có thể giúp điều hòa khí phong thủy trong nhà, có khả năng thu hút những dòng khí tích cực và làm tiêu bớt dòng khí tiêu cực cho môi trường xung quanh, trồng cây hồng môn trong nhà sẽ giúp không gian sống trở nên hài hòa và bình yên hơn.', 520000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0137', 1, 'LONI019', 'Cây kim ngân', 'Cây kim ngân 3 thân để bàn chậu đất nung LONI019', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 230000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0138', 1, 'MOAD003', 'Cây trầu bà lỗ', 'Cây trầu bà lỗ Monstera ‘Adansonii’ chậu gốm sứ MOAD003', 'Trầu bà lỗ có tên khoa học là, Monstera adansonii, hay còn được gọi là Swiss Cheese Vine, với bộ lá xanh mát hình chóp và dễ sống trong nhà nên loại cây này rất được ưu chuộng. Mỗi chiếc lá có đầy những lỗ hình bầu dục tự nhiên trông rất lạ mắt giúp nó khó nhầm lẫn với các loại cây khác.', 220000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0139', 1, 'AGSU003', 'Cây bao thanh thiên', 'Cây bao thanh thiên ‘Aglaonema Sumatra’ chậu đất nung AGSU003', 'Cây bao thanh thiên có bộ lá sặc sỡ và khỏe khoắn nên rất thích hợp để trang hoàng thêm điểm nhấn cho không gian sống. Loại cây này còn được xe là tượng trưng cho sự ngay thẳng, chính trực trong phong thủy. Khi trồng sẽ giúp thu hút năng lượng tích cực, xua đuổi cái xấu.', 380000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0140', 1, 'NTMX020', 'Cây cọ Nhật/ Thịnh vượng / Cung điện / Trầu bà', 'Chậu cây nội thất mix Cọ nhật, Thịnh vượng, Cung điện, Trầu bà NTMX020', 'Chậu cây nội thất được mix các loại cây dễ chăm sóc, thích nghi tốt với điều kiện thiếu sáng trong nhà. Sản phẩm được mix các loại cây Cọ Nhật, Cung điện, Thịnh vượng, Trầu bà thích hợp đặt tại phòng khách, lối hành lang, văn phòng, sảnh nhà hàng, khách sạn hoặc làm quà tặng.', 1200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0141', 1, 'RADE021', 'Cây hạnh phúc', 'Cây hạnh phúc một thân nhỏ đất nung RADE021', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa mang lại may mắn và hạnh phúc nên cây thường được chọn để làm cây trưng trong nhà hoặc làm quà tặng.', 650000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0142', 1, 'NTMX019', 'Cây đại lộc / vạn lộc / ngọc ngân / đô la', 'Chậu cây cảnh mix đại lộc, vạn lộc, ngọc ngân, đô la NTMX019', 'Chậu cây nội thất được mix các loại cây dễ chăm sóc, thích nghi tốt với điều kiện thiếu sáng trong nhà. Sản phẩm được mix các loại cây Đại lộc, Vạn lộc, Ngọc ngân, Đô la thích hợp đặt tại phòng khách, lối hành lang, văn phòng, sảnh nhà hàng, khách sạn hoặc làm quà tặng.', 6250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
