('CCNPLT0230', 1, 'CDBD202', 'Đa búp đỏ cẩm thạch', 'Cây đa búp đỏ cẩm thạch 1 thân chậu ươm BDCT003', 'Từ lâu cây Đa luôn là biểu tượng của sự trường tồn, sức sống dẻo dai, nơi chứa đựng thần quyền và tâm linh của con người. Chính lẽ đó mà trong phong thủy, Đa Búp Đỏ cũng mang ý nghĩa của sự may mắn, an lành.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05');
INSERT INTO `products` (`product_id`, `type`, `code`, `name`, `short_description`, `detailed_description`, `price`, `total_orders`, `total_ratings`, `overall_stars`, `is_returnable`, `created_at`, `updated_at`) VALUES
('CCNPLT0231', 1, 'LAMA002', 'Cây cô tòng đuôi lươn', 'Cây cô tòng đuôi lươn ‘Croton Captain Kidd’ chậu ươm LAMA002', 'Cô tòng đuôi lươn sở hữu bộ lá nhiều màu sắc rực rỡ, cùng những đường viền hoa văn độc đáo, chúng thường được trồng trang trí cho bồn hoa, ban công hoặc trước hiên nhà. Loại cây này đôi lúc được trồng trong nhà, nhưng đòi hỏi phải trồng nơi có nhiều ánh sáng, gần cửa sổ.', 65000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0232', 1, 'LAMA001', 'Cây cô tòng lá mít', 'Cây cô tòng lá mít ‘Croton Petra’ chậu ươm LAMA001', 'Cô tòng lá mít là loại có thể trồng được cả trong nhà lẫn ngoài trời. Với tán lá rực rỡ nhiều màu sắc như xanh lá, vàng, đỏ và cam, sẽ là một lựa chọn tuyệt vời để trang hoàng cho ngôi nhà thêm sinh động hơn.', 160000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0233', 1, 'AGRS001', 'Cây vạn lộc đỏ', 'Cây vạn lộc đỏ ‘Suksom’ chậu ươm AGRS001', 'Cây bao thanh thiên có bộ lá sặc sỡ và khỏe khoắn nên rất thích hợp để trang hoàng thêm điểm nhấn cho không gian sống. Loại cây này còn được xe là tượng trưng cho sự ngay thẳng, chính trực trong phong thủy. Khi trồng sẽ giúp thu hút năng lượng tích cực, xua đuổi cái xấu.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0234', 1, 'AGSW001', 'Cây ngọc bích', 'Cây ngọc bích ‘Super White’ chậu ươm AGSW001', 'Cây ngọc bích là là cây trong nhà được đánh giá cao về phong thuỷ, người ta quan niệm rằng cây Ngọc ngân mang đến tài lộc, may mắn khi đặt trên bàn làm việc hay trang trí trong văn phòng. Về mặt tinh thần cây Ngọc ngân rất thích hợp làm quà tặng tình yêu.', 160000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0235', 1, 'SCHE011', 'Ngũ gia bì cẩm thạch', 'Cây ngũ gia bì cẩm thạch cỡ nhỏ chậu ươm SCHE011', 'Cây ngũ gia bì là loại cây mang lại tài vận, thịnh vượng và may mắn, trồng trong nhà sẽ mang đến một không gian thư thái, trong lành cho gia đình.', 60000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0236', 1, 'RADE011', 'Cây hạnh phúc', 'Cây hạnh phúc cỡ trung chậu ươm RADE011', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa mang lại may mắn và hạnh phúc nên cây thường được chọn để làm cây trưng trong nhà hoặc làm quà tặng.', 220000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0237', 1, 'AGSU001', 'Cây bao thanh thiên', 'Cây bao thanh thiên ‘Aglaonema Sumatra’ chậu ươm AGSU001', 'Cây bao thanh thiên có bộ lá sặc sỡ và khỏe khoắn nên rất thích hợp để trang hoàng thêm điểm nhấn cho không gian sống. Loại cây này còn được xe là tượng trưng cho sự ngay thẳng, chính trực trong phong thủy. Khi trồng sẽ giúp thu hút năng lượng tích cực, xua đuổi cái xấu.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0238', 1, 'PHBZ024', 'Trầu bà Brazil', 'Cây trầu bà Brasil ‘Carnival’ leo cột chậu ươm PHBZ024', 'Trầu bà tỷ phú là loại cây nội thất cao cấp với những chiếc lá to có hình trái tim, màu cẩm thạch sang trọng luôn được săn đón trong giới mê cây cảnh. Trầu bà tỷ phú còn được xem là loại cây phong thủy, mang ý nghĩa về sự thành đạt và trù phú.', 330000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0239', 1, 'PHIL106', 'Trầu bà tỷ phú cẩm thạch', 'Cây trầu bà tỷ phú cẩm thạch ‘Burle Marx’ PHIL106', 'Trầu bà tỷ phú là loại cây nội thất cao cấp với những chiếc lá to có hình trái tim, màu cẩm thạch sang trọng luôn được săn đón trong giới mê cây cảnh. Trầu bà tỷ phú còn được xem là loại cây phong thủy, mang ý nghĩa về sự thành đạt và trù phú.', 650000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0240', 1, 'AGMO001', 'Cây sao sáng lá đậm', 'Cây sao sáng lá đậm ‘Galaxy’ chậu ươm AGMO001', 'Với bộ lá rực rỡ, có nền màu xanh đậm, bên trong là những đốm sáng tựa như những ngôi sao nên cây sao sáng thường được trồng để trang hoàng cho không gian nội thất. Loại cây này còn được xem là biểu tượng của sự may mắn, nguồn năng lượng tích cực, tài lộc và sự thịnh vượng.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0241', 1, 'CHLO001', 'Cây phượng hoàng lửa / Lục thảo lan', 'Cây phượng hoàng lửa Chlorophytum chậu ươm CHLO001', 'Cây phượng hoàng lửa là loại có sức sống rất bền bỉ, dễ chăm sóc và sống được nơi thiếu ánh sáng nên rất thích hợp để trồng trong nhà. Loại cây này sở hữu bộ lá màu xanh mướt, cùng phần thân màu cam ấm cúng nên rất thích hợp với các không gian tối giản.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0242', 1, 'TBDV501', 'Trầu bà đế vương kim cương', 'Cây trầu bà đế vương kim cương ‘Birkin’ TBDV501', 'Trầu Bà Đế Vương Kim Cương là một làn gió mới trong kiểng lá khi nói đến các loại cây trồng trong nhà đang thịnh hành và nó chắc chắn sẽ trêu ngươi bất kỳ người đam mê cây cảnh nào.', 125000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0243', 1, 'TBDV100', 'Cây trầu bà đế vương vàng', 'Cây trầu bà đế vương ‘Rojo’ mini chậu ươm TBDV100', 'Trầu Bà Đế Vương có tên tiếng Anh là Philodendron Imperial thuộc họ Araceae (Ráy), là dòng cây thân thảo, trong tự nhiên dòng cây này có thể cao hơn 1,5 m nhưng với mục đích trang trí thì MOW sẽ gửi đến các bạn cây với kích cỡ nhỏ để tiện đặt ở bất cứ đâu mình thích.', 75000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
