('CCNPLT0278', 1, 'CHRY004', 'Cau vàng Nhật Bản', 'Cây cau vàng Nhật Bản cỡ trung chậu đá mài CHRY004', 'Cây cau Nhật có tác dụng hút khí độc, làm sạch không khí trong nhà và môi trường xung quanh. Cây hấp thu chất ô nhiễm vào lá và chuyển các độc tố xuống rễ.', 700000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
('CCNPLT0279', 1, 'CHRY002', 'Cau lụa vàng Nhật Bản', 'Cây cau vàng Nhật Bản cỡ trung chậu ươm CHRY002', 'Cây cau Nhật có tác dụng hút khí độc, làm sạch không khí trong nhà và môi trường xung quanh. Cây hấp thu chất ô nhiễm vào lá và chuyển các độc tố xuống rễ.', 400000, NULL, NULL, NULL, 1, '2024-11-20 00:49:05', '2024-11-20 00:49:05'),
