('CCNPLT0358', 1, 'QUTA01', 'Cây quà tặng', 'Cây quà tặng mix chậu men sứ trắng QUTA001', 'Cây xanh sẽ là món quà tặng tuyệt vời mang lại nhiều ý nghĩa, giúp gắn kết giữa bạn với người được tặng.', 420000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0359', 1, 'cala013', 'Môn lính', 'Cây môn lính để bàn chậu sứ mini Caladium Hilo Beauty CALA013', 'Loài cây đuôi công này được trồng rất phổ biến trong nội thất với mục đích trang trí, họa tiết lá rất sang trọng.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0360', 1, 'CAMA004', 'Đuôi công lá đốm / Đuôi công khổng tước', 'Cây đuôi công khổng tước ‘Makoyana’ chậu sứ CAMA004', 'Loài cây đuôi công này được trồng rất phổ biến trong nội thất với mục đích trang trí, tạo thêm mảnh xanh và thanh lọc không khí, họa tiết lá rất sang trọng.', 320000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0361', 1, 'phil012', 'Trầu rồng', 'Cây trầu rồng leo trụ Philodendron Golden Dragon PHIL012', 'Là lựa chọn tuyệt với cho những không gian nội thất mở, tiểu cảnh nhiệt đới đa dạng.', 550000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0362', 1, 'ZAMI015', 'Cây kim tiền / Kim phát tài', 'Cây kim tiền cỡ trung chậu xi măng đá mài vuông ZAMI015', 'Cây kim tiền là loài thực vật họ nhà Araceae, có lá hình xương cá màu xanh mướt. Loài này rất dễ sống phát triển mạnh ở những nơi có độ ẩm cao, thường được trồng để trang trí trong phòng khách, làm quà tặng tân gia, khai trương cửa hàng.', 700000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0363', 1, 'mons014', 'Trầu bà lá xẻ', 'Cây Trầu bà Nam Mỹ Monstera nhiều thân chậu gốm họa tiết MONS014', 'Là loài cây sở hữu cho mình những chiếc lá được tạo nét như trái tim, càng lớn lá bắt đầu xuất hiện các đường xẻ một cách ngẫu nhiên.', 1200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0364', 1, 'CATH104', 'Cây đuôi phụng', 'Cây đuôi phụng lớn chậu men sứ trắng CATH104', 'Vẻ đẹp độc đáo của loài cây này khiến cho người ta phải gọi nó bằng cái tên là cây đuôi phụng, và theo “mặc định” thì nó luôn là một sự lựa chọn tuyệt hảo được dùng để trang trí cho không gian nội thất thêm phần sinh động.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0365', 1, 'CAMA005', 'Đuôi công khổng tước', 'Cây đuôi công khổng tước ‘Makoyana’ chậu đất nung CAMA005', 'Loài cây đuôi công này được trồng rất phổ biến trong nội thất với mục đích trang trí, tạo thêm mảnh xanh và thanh lọc không khí, họa tiết lá rất sang trọng.', 240000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0366', 1, 'cbtt001', 'Bao thanh thiên', 'Cây bao thanh thiên chậu gốm để bàn CBTT001', 'Sống lâu năm và thường mọc thành bụi, mang đặc điểm nổi bật nhờ các gân đỏ rõ nét trên nền lá màu xanh tía.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0367', 1, 'phil011', '• Công chúa hồng\r\n• Pink Princess', 'Cây Công Chúa Hồng Philodendron Pink Princess PHIL011', 'Philodendron Pink Princess với những mảng var màu hồng, mỗi chiếc lá với sự phân bổ màu sắc khác nhau chắc chắn sẽ được lòng mọi người.', 800000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0368', 1, 'cck001', 'Chuối kiểng', 'Cây chuối kiểng chậu xi măng đá mài CCK001', 'Cây sống tốt trong điều kiện khí hậu tại nước ta.', 750000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0369', 1, 'ctbh005', 'Trúc bách hợp', 'Cây trúc bách hợp chậu gốm có họa tiết CTBH005', 'Cây trúc bách hợp thường được làm quà tặng nhân dịp khánh thành, khai trương, tân gia, như là lời chúc may mắn đến với người nhận.', 750000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0370', 1, 'BUBU004', 'Bàng Đài Loan cẩm thạch', 'Cây bàng Đài Loan cẩm thạch chậu gốm sứ BUBU004', 'Cây Bàng Đài Loan Cẩm Thạch có lá nhỏ xinh và sắc xanh, viền trắng hồng rất khác biệt và nổi bật, giúp không gian trở nên độc đáo và sang trọng', 700000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0371', 1, 'crq001', 'Chuối rẻ quạt', 'Cây chuối rẻ quạt chậu gốm họa tiết CRQ001', 'Tuy cây chuối rẻ quạt thuộc giống cây ngoại nhập, nhưng cây vẫn sống tốt trong điều kiện khí hậu tại nước ta.', 750000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0372', 1, 'cptn008', 'Phát tài núi', 'Cây phát tài núi 2 tầng chậu sứ CPTN008', 'Cây phát tài núi rất thường được lựa chọn để làm quà tặng vào những dịp khai trương, lên nhà mới, văn phòng mới… với mong muốn đem lại nhiều tài lộc và may mắn cho người được tặng.', 1650000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0373', 1, 'cptn007', 'Phát tài núi', 'Cây phát tài núi nhiều nhánh chậu sứ họa tiết CPTN007', 'Cây phát tài núi rất thường được lựa chọn để làm quà tặng vào những dịp khai trương, lên nhà mới, văn phòng mới… với mong muốn đem lại nhiều tài lộc và may mắn cho người được tặng.', 3200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0374', 1, 'mons013', 'Cây Trầu Bà Lỗ', 'Cây Trầu Bà Lỗ chậu gốm để bàn Monstera adansonii Monkey Mask – MONS013', 'Monstera adansonii là cây thân thảo, thích nghi với bóng râm hoàn toàn, vì vậy chúng thường được trồng trong nhà, sức sống mạnh mẽ, dễ dàng chăm sóc và có thể tồn tại trong nhiều môi trường khác nhau, gốc nhiều nhánh và đốt.\r\nVới những chiếc lá xanh tươi, thân hình nhỏ nhắn trông rất dễ thương có thể sẽ rất phù hợp với các bạn nữ, nhân viên văn phòng trong lúc mệt mỏi có thể ngắm nhìn để giải tỏa phần nào áp lực công việc, cuộc sống.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0375', 1, 'SANS007', 'Lưỡi hổ ta', 'Cây lưỡi hổ để bàn SANS007', 'Lưỡi hổ có lẽ là loại cây nội thất quen thuộc nhất đối với những người yêu cây cảnh. Loài cây này có khả năng chịu nóng, chịu hạn tốt, sống được trong bóng râm và đặc biệt nó có khả năng lọc không khí rất tuyệt vời.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0376', 1, 'LICU007', 'Cọ Nhật', 'Cây cọ Nhật cây văn phòng LICU007', 'Cây cọ nhật là loại cây trong nhà có kiểu lá xòe rộng như những cánh quạt, giúp trang trí không gian thêm xanh mát. Nó còn là loại cây phong thủy tượng trưng cho sự giàu sang và tiền tài nên rất đáng trồng trên bàn làm việc.', 550000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0377', 1, 'MIX006', 'Cây văn phòng - cây quà tặng', 'Cây văn phòng – cây quà tặng MIX006', 'Combo cây văn phòng Mix006 có thể dễ dàng bài trí trong văn phòng, quán cafe, phòng khách…mà không phải lo về ngũ hành tương sinh, tương khắc.\r\nChậu cây văn phòng MIX006 là sản phẩm được MOW phối trộn với nhau một cách hài hòa và hợp lý. Điểm nhấn là cây Hoàng Hậu làm trung tâm, dưới chân là cây Phú Quý, cây Ngọc Ngân và cây thường xuân. Biểu trưng cho một bà Hoàng Hậu nhân từ ôm lấy những đứa còn, mong đem lại hạnh phúc và tài lộc cho gia chủ.', 1290000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0378', 1, 'TBTX004', 'Cây trầu bà thanh xuân / Cây trầu bà tay Phật', 'Cây trầu bà thanh xuân cỡ trung chậu đất nung TBTX004', 'Trầu bà thanh xuân là loại cây trong nhà độc đáo được mọi người ưa thích và trưng bày trang trí. Trầu Bà Thanh Xuân có ý nghĩa về mặt phong thủy nó còn có ý nghĩa may mắn, đem lại tài lộc, gia tăng vận khí, thể hiện ý chí vươn lên của gia chủ.', 520000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0379', 1, 'ARAC026', 'Cây môn thái', 'Cây môn Thái Caladium lindenii chậu đất nung ARAC026', 'Cây môn Thái sở hữu chất lá dày, khỏe khoắn, có form tai thỏ bắt mắt, cùng với sự tương phản giữa màu xanh thẩm của lá và sắc trắng ở gân lá, tạo ra một điểm nhấn riêng cho khu vườn của bạn. Loại cây này cực kì dễ chăm sóc và tốc độ phát triển cũng rất nhanh.', 420000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0380', 1, 'cdvd004', 'Đế vương đỏ', 'Cây Trầu bà đế vương đỏ chậu men lu sọc Philodendron Imperial Red CDVD004', 'Thân dạng thảo lớn, trong tự nhiên cây có thể leo được, nhưng đa số khi trồng chậu thì thân vươn thẳng chắc chắn, cao khoảng dưới 1m5.', 850000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0381', 1, 'CPTN006', 'Phát tài núi', 'Cây phát tài núi để sàn chậu sứ họa tiết CPTN006', 'Cây phát tài núi rất thường được lựa chọn để làm quà tặng vào những dịp khai trương, lên nhà mới, văn phòng mới… với mong muốn đem lại nhiều tài lộc và may mắn cho người được tặng.', 650000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0382', 1, 'SANS035', 'Lưỡi hổ vàng', 'Cây lưỡi hổ vàng chậu đá xi măng đá mài SANS035', 'Lưỡi hổ có lẽ là loại cây nội thất quen thuộc nhất đối với những người yêu cây cảnh. Loài cây này có khả năng chịu nóng, chịu hạn tốt, sống được trong bóng râm và đặc biệt nó có khả năng lọc không khí rất tuyệt vời.', 600000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0383', 1, 'pepe001', 'Hạnh tiên thảo đỏ', 'Cây Hạnh tiên thảo đỏ chậu gốm để bàn Peperomia Caperata PEPE001', 'Không đòi hỏi gắt gao trong việc chăm sóc nên rất thích hợp trồng chậu trang trí nội lẫn ngoại thất.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0384', 1, 'CDBD003', 'Đa búp đỏ', 'Cây đa búp đỏ một thân chậu xi măng CDBD003', 'Từ lâu cây Đa luôn là biểu tượng của sự trường tồn, sức sống dẻo dai, nơi chứa đựng thần quyền và tâm linh của con người. Chính lẽ đó mà trong phong thủy, Đa Búp Đỏ cũng mang ý nghĩa của sự may mắn, an lành.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0385', 1, 'CHEI001', 'Môn chong chóng', 'Cây môn chong chóng var chậu đất nung CHEI001', 'Môn chong chóng đột biến là loại cây kiểng lá có màu sắc sọc trắng gây ấn tượng, mang ý nghĩa đem lại sự may mắn cho chủ sở hữu.', 380000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0386', 1, 'MONS012', 'Trầu bà lá xẻ', 'Cây Trầu bà Nam Mỹ – Monstera Borsigiana – MONS012', 'Là loài cây sở hữu cho mình những chiếc lá được tạo nét như trái tim, càng lớn lá bắt đầu xuất hiện các đường xẻ một cách ngẫu nhiên.', 3500000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0387', 1, 'PHIL202', 'Trầu bà đế vương kim cương', 'Cây trầu bà đế vương kim cương chậu đất nung PHIL202', 'Gây ấn tượng với những đường sọc trắng đẹp mắt, mang ý nghĩa phong thủy may mắn và tiền tại cho chủ sở hữu.', 280000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0388', 1, 'MONS010-1', 'Trầu bà lá xẻ', 'Cây Trầu bà Nam Mỹ – Monstera Borsigiana – MONS011', 'Là loài cây sở hữu cho mình những chiếc lá được tạo nét như trái tim, càng lớn lá bắt đầu xuất hiện các đường xẻ một cách ngẫu nhiên.', 5000000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0389', 1, 'CPTN005', 'Phát tài núi', 'Cây phát tài núi 2 tầng chậu đá mài CPTN005', 'Cây phát tài núi rất thường được lựa chọn để làm quà tặng vào những dịp khai trương, lên nhà mới, văn phòng mới… với mong muốn đem lại nhiều tài lộc và may mắn cho người được tặng.', 1400000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0390', 1, 'PHIL800', 'Philodendron splendid', 'Cây Philodendron splendid leo cột chậu xi măng PHIL801', 'Philodendron splendid là loại cây kiểng lá rất được yêu thích bởi nó có phiến lá hình tim thuôn dài và mặt lá nhung mịn rất đẹp mắt. Những đường gân màu xanh sáng, có vệt hồng ở điểm giao giữa cuống và lá tạo thêm sự sắc sảo.', 3850000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0391', 1, 'MONS010', 'Trầu bà lá xẻ', 'Cây Trầu bà Nam Mỹ – Monstera Borsigiana – MONS010', 'Là loài cây sở hữu cho mình những chiếc lá được tạo nét như trái tim, càng lớn lá bắt đầu xuất hiện các đường xẻ một cách ngẫu nhiên.', 7500000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0392', 1, 'CHAM006', 'Cau Hawaii', 'Cây cau Hawaii chậu xi măng đá mài CHAM006', 'Cau Hawaii là một loại cây cây cảnh nội thất phổ biến, thích hợp để trưng bày trong các không gian nội thất sang trọng. Loại cây này có sức sống mãnh liệt, dễ chăm sóc, có bộ lá xanh mướt và mang ý nghĩa rất tốt trong phong thủy.', 750000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0393', 1, 'CPTN004', 'Phát tài núi', 'Cây phát tài núi 2 tầng chậu gốm sứ CPTN004', 'Cây phát tài núi rất thường được lựa chọn để làm quà tặng vào những dịp khai trương, lên nhà mới, văn phòng mới… với mong muốn đem lại nhiều tài lộc và may mắn cho người được tặng.', 1550000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0394', 1, 'AGLA004-1', 'Cây vạn lộc', 'Cây Vạn Lộc để bàn Aglaonema Lady Valentine AGLA010', 'Cây vạn lộc (Aglaonema Lady Valentine) là dòng cây cảnh nội thất dễ chăm sóc, có thể sống được trong điều kiện ánh sáng thấp. Với một hình dạng lá độc đáo, bên trên là những họa tiết đẹp mắt giống như tranh vẽ giúp tạo thêm điểm nhấn cho không gian nội thất. Kích thước nhỏ gọn của cây vạn lộc thủy sinh rất thích hợp để đặt trang trí trên bàn hoặc trên tủ kệ.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0395', 1, 'AGLA009', 'Ngân hậu; Minh Ty Rằn', 'Cây Ngân Hậu để bàn Aglaonema Marantifolium AGLA009', 'Cây ngân hậu đại diện cho sự sang chảnh, quyền cao quý tộc giống như cái tên hoàng gia của nó vậy.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0396', 1, 'AGLA008', 'Ngân hậu; Minh Ty Rằn', 'Cây Ngân Hậu văn phòng Aglaonema Marantifolium AGLA008', 'Cây ngân hậu đại diện cho sự sang chảnh, quyền cao quý tộc giống như cái tên hoàng gia của nó vậy.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0397', 1, 'NTMX017', 'Cây phát tài / Vạn lộc / Ngọc ngân / Đô la / Trúc vàng', 'Chậu cây mix phát tài núi, trúc Nhật, vạn lộc, ngọc ngân, đô la NTMX017', 'Chậu cây nội thất được mix các loại cây dễ chăm sóc, thích nghi tốt với điều kiện thiếu sáng trong nhà. Sản phẩm được mix các loại cây Phát tài, Vạn lộc, Ngọc ngân, Đô la, Trúc Nhật mini thích hợp thể đặt tại phòng khách, lối hành lang, văn phòng làm việc, sảnh nhà hàng, khách sạn hoặc làm quà tặng.', 1250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0398', 1, 'MIX003', 'Cây văn phòng', 'Cây văn phòng – cây quà tặng MIX003', 'Chậu cây văn phòng MIX003 là sản phẩm được MOW phối trộn với nhau một cách hài hòa và hợp lý.\r\nLưỡi hổ cao làm chủ đạo, dưới chân là Vạn Lộc đỏ, Ngọc Ngân màu trắng ngọc và cây Đô La bao quanh gốc làm điểm tựa cho cây Lưỡi Hổ phát triển cũng như sự phát triển Tài Lộc của gia chủ.\r\nVới chậu cây văn phòng Mix003 này chúng ta có thể dễ dàng bài trí trong văn phòng, quán cafe, phòng khách…mà không phải lo về ngũ hành tương sinh, tương khắc.', 890000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0399', 1, 'DRAC401', 'Cây huyết dụ kiếm', 'Cây huyết dụ kiếm chậu xi măng đá mài DRAC401', 'Cây huyết Dụ là một loại cây phong thủy có tác dụng mang lại may mắn, giữ tiền của và tài lộc cho gia chủ. Bên cạnh đó, cây huyết dụ được trồng hòng để ngăn chặn, xua đuổi tà ma tấn công vào nhà.', 420000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0400', 1, 'SANS034', 'Lưỡi hổ xanh', 'Cây lưỡi hổ xanh chậu đá xi măng vàng SANS034', 'Lưỡi hổ có lẽ là loại cây nội thất quen thuộc nhất đối với những người yêu cây cảnh. Loài cây này có khả năng chịu nóng, chịu hạn tốt, sống được trong bóng râm và đặc biệt nó có khả năng lọc không khí rất tuyệt vời.', 950000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0401', 1, 'PHIL026', 'Cây cung đàn xanh', 'Cây cung đàn chậu đất nung để bàn PHIL026', 'Cung đàn xanh là loại cây nội thất được săn đón ráo riết bởi cộng đồng kiểng lá trên thế giới bởi hình dáng lá răng cưa khá thú vị của nó. Nếu bạn đang muốn làm đa dạng bộ sưu tập cây trong nhà của mình thì em nó là một sự lựa chọn rất tuyệt vời.', 260000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0402', 1, 'SANS006', 'Lưỡi hổ ta', 'Cây lưỡi hổ vàng chậu xi măng SANS006', 'Lưỡi hổ có lẽ là loại cây nội thất quen thuộc nhất đối với những người yêu cây cảnh. Loài cây này có khả năng chịu nóng, chịu hạn tốt, sống được trong bóng râm và đặc biệt nó có khả năng lọc không khí rất tuyệt vời.', 860000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0403', 1, 'CHRY007', 'Cau vàng Nhật Bản', 'Cây cau vàng Nhật Bản cỡ trung chậu xi măng xi măng CHRY007', 'Cây Cau Nhật là loại cây cảnh đẹp ngoài việc dùng làm trang trí nội thất, ngoại thất làm tô điểm cho không gian sống thì cây còn có khả năng hấp thụ các độc tố hóa học và có một ý nghĩa vô cùng đặc biệt trong việc lọc khí độc, trừ tà.', 1200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0404', 1, 'SANS005', 'Lưỡi hổ ta', 'Cây lưỡi hổ vàng chậu đá mài trụ tròn SANS005', 'Lưỡi hổ có lẽ là loại cây nội thất quen thuộc nhất đối với những người yêu cây cảnh. Loài cây này có khả năng chịu nóng, chịu hạn tốt, sống được trong bóng râm và đặc biệt nó có khả năng lọc không khí rất tuyệt vời.', 780000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0405', 1, 'LYRA023', 'Cây bàng Singapore', 'Cây bàng Singapore cao 2m dáng tree chậu sứ LYRA023', 'Bàng Singapore là loại cây nội thất đang được ưa chuộng nhất hiện nay bởi cây có khả năng cải thiện chất lượng không khí mang lại sự xanh mát và sang trọng cho không gian.', 2200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0406', 1, 'MIX002', 'Cây văn phòng - cây quà tặng', 'Cây văn phòng – cây quà tặng MIX002', 'Chậu cây văn phòng MIX002 là sản phẩm được MOW phối trộn với nhau một cách hài hòa và hợp lý.\r\nPhát Tài Núi dáng tree làm chủ đạo, dưới chân là Vạn Lộc đỏ và thường xuân bao quanh gốc làm điểm tựa cho cây Phát Tài Núi phát triển cũng như sự phát triển Tài Lộc của gia chủ.\r\nVới chậu cây văn phòng Mix002 này chúng ta có thể dễ dàng bài trí trong văn phòng, quán cafe, phòng khách…mà không phải lo về ngũ hành tương sinh, tương khắc.', 1550000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0407', 1, 'ALOCA007', 'Rồng bạc', 'Cây Rồng Bạc để bàn – Alocasia Baginda Silver Dragon – ALOCA007', 'Bạn không cần dành quá nhiều thời gian cho Rồng Bạc nhưng vẫn ngắm nhìn được vẻ đẹp của nó mỗi ngày.', 280000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0408', 1, 'PHIL010', 'Trầu bà tim xanh', 'Cây Trầu Bà Tim Xanh để bàn – Philodendron Scandens – PHIL010', 'Trầu bà tim xanh (Philodendron scandens) sở hữu những chiếc lá màu xanh bóng hình trái tim quyến rũ, có thể trồng theo phong cách thả xuống trên kệ trang trí, trên tủ sách hoặc được treo lơ lửng, cũng có thể được gắn vào cột leo đẹp mắt. Chúng có khả năng sinh trưởng nhanh chóng và rất dễ chăm sóc, chỉ cần tưới một lần một tuần.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0409', 1, 'ALOCA006', 'Môn quan âm', 'Cây Môn quan âm để bàn – Alocasia Polly – ALOCA006', 'Điểm nổi bật nhất ở Môn Quan Âm là hình dáng lá độc đáo. Với những chiếc lá hình tim, nổi bật lên bề mặt lá là những đường gân dày, hòa cùng sắc tím trông vô cùng bắt mắt, độc đáo.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0410', 1, 'PHIL009', 'Trầu Bà Đế Vương Vàng', 'Cây trầu bà đế vương vàng chậu xi măng PHIL009', 'Trầu bà đế vương vàng có kích thước lá to nên rất thích hợp để trang trí trong phòng khách, quầy tiếp tân, quán cafe, nhà hàng… màu vàng của lá vừa đẹp, vừa sang và vừa mang ý nghĩa phong thủy rất tốt.', 450000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0411', 1, 'LYRA031', 'Cây bàng Singapore', 'Cây bàng Singapore 2 thân chậu đá mài LYRA031', 'Là loại cây nội thất đang được ưa chuộng nhất hiện nay bởi cây có khả năng cải thiện chất lượng không khí mang lại sự xanh mát và sang trọng cho không gian.', 800000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0412', 1, 'SAZE001', 'Lưỡi hổ xanh', 'Cây lưỡi hổ xanh ‘Zeylanica’ chậu ươm SAZE001', 'Lưỡi hổ có lẽ là loại cây nội thất quen thuộc nhất đối với những người yêu cây cảnh. Loài cây này có khả năng chịu nóng, chịu hạn tốt, sống được trong bóng râm và đặc biệt nó có khả năng lọc không khí rất tuyệt vời.', 150000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0413', 1, 'CHP004', 'Cây hạnh phúc', 'Cây hạnh phúc chậu bầu đen CHP004', 'Cây dễ sinh trưởng phát triển mọi diều kiện thời tiết khác nhau nên rất dễ trồng và chăm sóc. Cây sống tốt trong điều kiện có điều hoà và môi trường thiếu sáng nên chúng ta có thể dễ dàng lựa chọn cây trang trí để bàn cho không gian làm việc trở nên xanh mát và ấm áp.', 990000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0414', 1, 'CALA009', 'Đuôi công táo xanh', 'Cây đuôi công táo xanh để bàn chậu sứ CALA009', 'Với tán lá tròn trịa xoè rộng phủ xanh cả khu vườn nhỏ, bạn chỉ cần trồng vài cây Orbifolia là không khí tropical mát mẻ đã về với không gian nhà rồi đó.', 320000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0415', 1, 'EANJ004', 'Cây trầu bà cẩm thạch', 'Cây trầu bà cẩm thạch ‘Njoy’ chậu sứ EANJ004', 'Cây trầu bà cẩm thạch (Epipremnum ‘Njoy’) là một dòng trầu bà mới được du nhập, có bộ lá màu loang kem xanh độc đáo. Cũng tương tự cây trầu bà, nó cũng là giống cây có sức sống cực kỳ khỏe mạnh, với bộ lá xanh quanh năm và tốc độ phát triển khá nhanh.', 160000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0416', 1, 'RHAP002', 'Trầu bà lá xẻ mini form Thái', 'Cây Monstera form Thái mini chậu gốm Rhaphidophora Tetrasperma RHAP002', 'Thường bị nhầm lẫn với Monstera deliciosa phiên bản mini vì hình dáng lá trông khá tương đồng.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0417', 1, 'scin001', 'Trầu bà lụa', 'Cây Trầu bà lụa chậu gốm để bàn Scindapsus pictus Exotica SCIN001', 'Dễ dàng được lòng những người thích cây treo, cây leo hoặc các giống cây quý hiếm vì nó có hoa văn và màu sắc đẹp.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0418', 1, 'DRSU001', 'Trúc Nhật vàng', 'Cây trúc Nhật vàng để bàn chậu đất nung DRSU001', 'Với kích thước nhỏ gọn, Trúc Nhật vàng rất thích hợp đặt trên bàn làm việc, kệ trang trí hoặc văn phòng. Trúc Nhật thuộc có đặc điểm phát triển như thân tre, với tán lá màu vàng đốm rất đẹp mắt, giúp tăng thêm sức sống cho không gian sống của bạn.', 145000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0419', 1, 'sans002-1-1', 'Cây phát lộc', 'Cây phát lộc chậu sứ để bàn DRAC001', 'Cây phát lộc là loại cây phong thủy để bàn có khả năng đem lại vận khí tốt cho sức khỏe, tình yêu và sự thịnh vượng. Loại cây này rất thích hợp dùng để làm quà tặng, bởi nó mang ý nghĩa như lời chúc phát triển sự nghiệp dành cho người được tặng.', 135000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0420', 1, 'mons009', 'Trầu bà lá xẻ', 'Cây Trầu bà Nam Mỹ Monstera nhiều thân chậu xi măng đen MONS009', 'Là loài cây sở hữu cho mình những chiếc lá được tạo nét như trái tim, càng lớn lá bắt đầu xuất hiện các đường xẻ một cách ngẫu nhiên.', 1200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0421', 1, 'cala008', 'Đuôi công nữ thần xanh', 'Cây Đuôi công nữ thần xanh để bàn Green Goddess CALA008', 'Loài cây đuôi công này được trồng rất phổ biến trong nội thất với mục đích trang trí, tạo thêm mảnh xanh và thanh lọc không khí.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0422', 1, 'CATH047', 'Đuôi công lá đốm', 'Cây Đuôi công đốm chậu men sứ để bàn Calathea Makoyana CATH047', 'Loài cây đuôi công này được trồng rất phổ biến trong nội thất với mục đích trang trí, tạo thêm mảnh xanh và thanh lọc không khí.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0423', 1, 'dief003', 'Danh dự', 'Cây Danh dự chậu xi măng đá mài Dieffenbachia sp DIEF003', 'Là một trong các loại cây được nhiều người ưa thích bởi mang lại nhiều tác dụng hữu ích, không những thế còn có nghĩa phong thủy tốt.', 400000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0424', 1, 'cdvd003', 'Đế vương đỏ', 'Cây Trầu bà đế vương đỏ chậu men đen bóng CDVD003', 'Thân dạng thảo lớn, trong tự nhiên cây có thể leo được, nhưng đa số khi trồng chậu thì thân vươn thẳng chắc chắn, cao khoảng dưới 1m5.', 550000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0425', 1, 'phil008', 'Trầu bà cánh én', 'Cây Trầu bà cánh én để bàn chậu mini Philodendron Squamiferum PHIL008', 'Ngoài vẻ đẹp và làm cảnh, trong một số nền văn hóa, cây Philodendron tượng trưng cho sức khỏe và sự dồi dào. Nó được biết đến là một dấu hiệu của sự may mắn.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0426', 1, 'CHAM002', 'Cau tiểu trâm', 'Cây cau tiểu trâm mini chậu đất nung CHAM003', 'Công việc đòi hỏi bạn phải tiếp xúc hằng ngày với các thiết bị hiện đại có nhiều bức xạ điện tử như máy tính, máy in, điện thoại khiến cho tình trạng sức khỏe bị ảnh hưởng, dẫn đến giảm chất lượng công việc.', 220000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0427', 1, 'agla006', 'Ngân hậu; Minh Ty Rằn', 'Cây Ngân hậu chậu sứ Aglaonema Marantifolium AGLA006', 'Cây ngân hậu đại diện cho sự sang chảnh, quyền cao quý tộc giống như cái tên hoàng gia của nó vậy.', 580000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0428', 1, 'CHPD002', 'Cây ánh dương', 'Cây huyết phất dụ chậu sứ để bàn CHPD002', 'Là cây thân gỗ mọc theo bụi thường xanh, dáng thân và tán lá rất đẹp thu hút người nhìn.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0429', 1, 'phil007', 'Trầu bà tỷ phú', 'Cây Trầu bà tỷ phú chậu gốm để bàn Philodendron Burle Marx PHIL007', 'Thường bị nhầm lẫn với Monstera deliciosa phiên bản mini vì hình dáng lá trông khá tương đồng.', 300000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0430', 1, 'rhap001', 'Trầu bà lá xẻ mini form Thái', 'Cây lá xẻ form Thái mini chậu gốm Rhaphidophora Tetrasperma RHAP001', 'Thường bị nhầm lẫn với Monstera deliciosa phiên bản mini vì hình dáng lá trông khá tương đồng.', 500000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0431', 1, 'SANS003', 'Lưỡi hổ ta', 'Cây lưỡi hổ ta chậu đất nung Sansevieria trifasciata Laurentii SANS003', 'Lưỡi hổ có lẽ là loại cây nội thất quen thuộc nhất đối với những người yêu cây cảnh. Loài cây này có khả năng chịu nóng, chịu hạn tốt, sống được trong bóng râm và đặc biệt nó có khả năng lọc không khí rất tuyệt vời.', 150000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0432', 1, 'phil006', 'Mayoi', 'Cây Philodendron Mayoi chậu gốm mini để bàn PHIL006', 'Là một trong những philodendron được yêu thích gần đây bởi vẻ đẹp của những chiếc lá khi trồng trong nhà hay ngoài trời', 450000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0433', 1, 'aloca005', 'Môn quan âm', 'Cây Môn quan âm chậu gốm đen để bàn Alocasia Amazonica Polly ALOCA005', 'Điểm nổi bật nhất ở Môn Quan Âm là hình dáng lá độc đáo. Với những chiếc lá hình tim, nổi bật lên bề mặt lá là những đường gân dày, hòa cùng sắc tím trông vô cùng bắt mắt, độc đáo.', 500000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0434', 1, 'PINA001', 'Cây tuyết tùng', 'Cây tuyết tùng chậu sứ trắng để bàn PINA001', 'Tuyết tùng là một trong những loại cây phong thủy để bàn được nhiều người yêu thích bởi vẻ đẹp mộc mạc, cùng những ý nghĩa tốt đẹp mà nó mang lại. Lá tuyết tùng thon gọn, ôm lấy nhau và rủ xuống tựa như những bông tuyết đang đọng lại trên lá', 200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0435', 1, 'aloca004', 'Ráy voi', 'Cây ráy voi lớn chậu xi măng đá mài Alocasia Macrorrhizos ALOCA004', 'Cây nội thất xu hướng mới của lĩnh vực decor hiện nay, được nhiều các bạn trẻ chưng diện trong các shop, quán cà phê', 520000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0436', 1, '', 'Cây Ổ Rồng', 'Cây Ổ Rồng treo tường Staghorn Fern STAG002', 'Cây Ổ Rồng được dùng để làm đẹp khoảng không ở khu vườn. Loài cây không thể thiếu cho những ai muốn tạo nên một khu rừng nhiệt đới thực thụ.', 450000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0437', 1, 'LYRA015', 'Cây bàng Singapore', 'Cây bàng Singapore để bàn chậu men sứ LYRA015', 'Bàng Singapore là loại cây nội thất đang được ưa chuộng nhất hiện nay bởi cây có khả năng cải thiện chất lượng không khí mang lại sự xanh mát và sang trọng cho không gian.', 280000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0438', 1, 'MONS008', 'Cây Trầu Bà Lỗ', 'Cây Trầu Bà Lỗ để bàn Monstera adansonii Monkey Mask – MONS008', 'Monstera adansonii là cây thân thảo, thích nghi với bóng râm hoàn toàn, vì vậy chúng thường được trồng trong nhà, sức sống mạnh mẽ, dễ dàng chăm sóc và có thể tồn tại trong nhiều môi trường khác nhau, gốc nhiều nhánh và đốt.\r\nVới những chiếc lá xanh tươi, thân hình nhỏ nhắn trông rất dễ thương có thể sẽ rất phù hợp với các bạn nữ, nhân viên văn phòng trong lúc mệt mỏi có thể ngắm nhìn để giải tỏa phần nào áp lực công việc, cuộc sống.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0439', 1, 'ZAMI011', 'Cây kim tiền', 'Cây kim tiền cỡ trung chậu sứ ZAMI011', 'Cây kim tiền là loài thực vật họ nhà Araceae, có lá hình xương cá màu xanh mướt. Loài này rất dễ sống phát triển mạnh ở những nơi có độ ẩm cao, thường được trồng để trang trí trong phòng khách, làm quà tặng tân gia, khai trương cửa hàng.', 650000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0440', 1, 'DVX002', 'Cây Trầu Bà Đế Vương Xanh', 'Cây Trầu Bà Đế Vương Xanh Để Bàn DVX002', 'Trầu Bà Đế Vương có tên tiếng Anh là Philodendron Imperial thuộc họ Araceae (Ráy). Nguồn gốc của cây từ đảo Solomon, nguyên sinh ở Indonesia, là dòng cây thân thảo, trong tự nhiên dòng cây này có thể cao hơn 1.5m, nhưng với mục đích trang trí thì MOW sẽ gửi đến các bạn cây với kích cỡ nhỏ để tiện đặt ở bất cứ đâu mình thích.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0441', 1, 'CDBD002', 'Đa búp đỏ', 'Cây đa búp đỏ để bàn 3 thân chậu sứ CDBD002', 'Từ lâu cây Đa luôn là biểu tượng của sự trường tồn, sức sống dẻo dai, nơi chứa đựng thần quyền và tâm linh của con người. Chính lẽ đó mà trong phong thủy, Đa Búp Đỏ cũng mang ý nghĩa của sự may mắn, an lành.', 550000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0442', 1, 'RADE008', 'Cây hạnh phúc', 'Cây hạnh phúc để bàn chậu sứ RADE008', 'Cây dễ sinh trưởng phát triển mọi diều kiện thời tiết khác nhau nên rất dễ trồng và chăm sóc. Cây sống tốt trong điều kiện có điều hoà và môi trường thiếu sáng nên chúng ta có thể dễ dàng lựa chọn cây trang trí để bàn cho không gian làm việc trở nên xanh mát và ấm áp.', 150000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0443', 1, 'TBTX003', 'Cây trầu bà thanh xuân / Cây trầu bà tay Phật', 'Cây trầu bà thanh xuân chậu gốm TBTX003', 'Trầu bà thanh xuân là loại cây trong nhà độc đáo được mọi người ưa thích và trưng bày trang trí. Trầu Bà Thanh Xuân có ý nghĩa về mặt phong thủy nó còn có ý nghĩa may mắn, đem lại tài lộc, gia tăng vận khí, thể hiện ý chí vươn lên của gia chủ.', 500000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0444', 1, 'LONI011', 'Cây kim ngân', 'Cây kim ngân một thân trụ lớn chậu sứ trắng LONI011', 'Cây Kim Ngân là loại cây cảnh trong nhà được trồng phổ biến trên khắp thế giới, nó có sức ảnh hưởng tới mức mà hầu như ai cũng tin rằng khi trồng có thể mang lại nhiều may mắn trong cuộc sống, công việc hoặc làm ăn.', 450000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0445', 1, 'CTN001', 'Cây hoàng hậu, cây thanh ngân', 'Cây thanh ngân – hoàng hậu để bàn CTN001', 'Cây Thanh Lam thường mọc thành bụi lớn và có tán lá rộng. Thân và lá của cây mọc vươn thẳng. Phần thân của cây có màu trắng, phần lá có có màu xanh trắng. Phần rìa lá có màu xanh đậm, phần chính giữa lá có màu trắng. Lá cây có hình bầu dục với các gân và sống lá rõ.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0446', 1, 'ZAMI021', 'Cây kim tiền', 'Cây kim tiền cỡ trung chậu men sứ heritage ZAMI021', 'Cây kim tiền là loài thực vật họ nhà Araceae, có lá hình xương cá màu xanh mướt. Loài này rất dễ sống phát triển mạnh ở những nơi có độ ẩm cao, thường được trồng để trang trí trong phòng khách, làm quà tặng tân gia, khai trương cửa hàng.', 650000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0447', 1, 'agla005', 'Ngân hậu; Minh Ty Rằn', 'Cây Ngân hậu chậu men sứ Aglaonema Marantifolium AGLA005', 'Cây ngân hậu đại diện cho sự sang chảnh, quyền cao quý tộc giống như cái tên hoàng gia của nó vậy.', 380000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0448', 1, 'TBDV401', 'Cây trầu bà đế vương vàng', 'Cây trầu bà đế vương vàng chậu ươm TBDV401', 'Trầu Bà Đế Vương có tên tiếng Anh là Philodendron Imperial thuộc họ Araceae (Ráy), là dòng cây thân thảo, trong tự nhiên dòng cây này có thể cao hơn 1,5 m nhưng với mục đích trang trí thì MOW sẽ gửi đến các bạn cây với kích cỡ nhỏ để tiện đặt ở bất cứ đâu mình thích.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0449', 1, 'sans002', 'Lưỡi hổ Thái kim cương đen', 'Cây Lưỡi hổ kim cương đen để bàn Sansevieria Trifasciata Black Diamond SANS002', 'Lưỡi hổ “Black Diamond” không có nhiều vằn như những loại lưỡi hổ khác, nhưng nó lại sở hữu bộ lá màu xanh lá bóng mượt ấn tượng, mang lại nét cứng cáp tự nhiên cho không gian nội thất.', 320000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0450', 1, 'sans001', 'Ngà voi', 'Cây Ngà voi chậu gốm để bàn Sansevireria Cylindrica SANS001', 'Hình dáng bên ngoài độc đáo như chính tên gọi, bởi giống như những chiếc ngà voi vươn thẳng lên cao.', 320000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0451', 1, 'CATH546', 'Đuôi công xương cá', 'Cây Đuôi công xương cá Calathea burle-marxii CATH546', 'Cây đuôi công thường được chọn để mang lại không gian xanh mát cho phòng làm việc, phòng làm việc, văn phòng, quán cafe,… Bởi loài cây có những đường vân sọc độc đáo, cùng màu xanh dễ chịu giúp căn phòng trở nên tươi mát và sang trọng hơn.', 360000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0452', 1, 'chob001', 'Cánh bướm xanh', 'Cây Cánh bướm xanh để bàn chậu gốm họa tiết Christia Obcordata CHOB001', 'Hiện đang rất được ưa chuộng nhờ vẻ đẹp hoang dại với những sọc đỏ tía trên nền xanh hình tam giác , tạo tổng thể như những cánh bướm phất phơ trước gió.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0453', 1, '', 'Trầu bà đế vương kim cương', 'Cây trầu bà đế vương kim cương để bàn PHIL201', 'Gây ấn tượng với những đường sọc trắng đẹp mắt, mang ý nghĩa phong thủy may mắn và tiền tại cho chủ sở hữu.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0454', 1, 'bego001', 'Thu hải đường', 'Cây Thu hải đường băng giá để bàn Begonia Ice Storm BEGO001', 'Những đốm trắng nổi rõ trên mặt lá như những giọt nước dưới cái lạnh mùa đông kết tinh thành băng đá.', 480000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0455', 1, 'DITS002', 'Vạn niên thanh', 'Cây vạn niên thanh ‘Tropic Snow’ chậu sứ cao DITS002', 'Với bộ lá rực rỡ, có nền màu xanh đậm, bên trong là những đốm sáng tựa như những ngôi sao nên cây sao sáng thường được trồng để trang hoàng cho không gian nội thất. Loại cây này còn được xem là biểu tượng của sự may mắn, nguồn năng lượng tích cực, tài lộc và sự thịnh vượng.', 650000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0456', 1, 'aloca002', 'Môn nhung', 'Cây Môn Nhung chậu gốm để bàn Alocasia Micholitziana Frydek ALOCA002', 'Làm cho ngôi nhà, khu vườn của bạn sinh động thêm với những chiếc lá rộng và mượt như nhung, kèm theo đó là những đường gân trắng nổi bật.', 1400000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0457', 1, 'PTDL003', 'Cây phát tài đài loan', 'Cây phát tài Đài Loan để bàn chậu sứ Geometric PTDL003', 'Cây phát tài Đài Loan lá dài màu xanh sọc vàng, mặt lá bóng, mép lá nguyên, bẹ lá ôm sát thân. Thân gỗ nhỏ sống lâu năm, chịu bóng râm tốt, sống được ngoài trời. Phát triển chậm và ít khi phân nhánh ngang', 240000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0458', 1, 'ZAMI014', 'Cây kim tiền / Kim phát tài', 'Cây kim tiền cỡ lớn chậu xi măng remy màu đen ZAMI014', 'Cây kim tiền là loài thực vật họ nhà Araceae, có lá hình xương cá màu xanh mướt. Loài này rất dễ sống phát triển mạnh ở những nơi có độ ẩm cao, thường được trồng để trang trí trong phòng khách, làm quà tặng tân gia, khai trương cửa hàng.', 850000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0459', 1, 'RADE007', 'Cây hạnh phúc', 'Cây hạnh phúc một thân cỡ trung chậu đá mài RADE007', 'Cây hạnh phúc là dòng cây cảnh đẹp, sức sống khỏe mạnh, dễ chăm sóc. Trên cây có những tán lá xanh tươi, mượt mà thể hiện cho sự hi vọng và niềm tin mạnh mẽ. Với ý nghĩa may mắn hạnh phúc và dễ chăm sóc mà cây được chọn để làm cây trưng trong nhà hay làm quà tặng.', 990000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0460', 1, 'agsc001', 'Thanh tâm', 'Cây Thanh tâm chậu gốm Aglaonema Modestum Schott AGSC001', 'Một trong những loại cây cảnh trong nhà rất có giá trị về mặt phong thủy đang được nhiều người đam mê cây cảnh săn đón.', 400000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0461', 1, 'phil005', 'Hồng hạc cam', 'Cây Hạc cam để bàn chậu xi măng đá mài Philodenron Billietiae PHIL005', 'Với chiếc lá gây ấn tượng khá mạnh với nhiều người chơi kiểng nên hiện tại Billietiae đang dần phổ biến tại Việt Nam.', 2000000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0462', 1, 'BMHT002', 'Bạch mã hoàng tử', 'Cây Bạch mã hoàng tử chậu trắng BMHT002', 'Là loại cây nội thất đẹp thường được trồng để trang trí trong văn phòng, nhà ở, quán cafe, phòng làm việc với nhiều ý nghĩa phong thủy tốt đẹp.', 450000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0463', 1, 'SPI002', 'Cây cỏ lan chi / cây dây nhện', 'Cây cỏ lan chi để bàn chậu gốm trắng SPI002', 'Cỏ Lan Chi (hay còn gọi là Cỏ Nhện) là loại cây rất dễ sống với điều kiện khí hậu nóng tại Việt Nam. Đặc biệt, nó nằm trong top các loại cây lọc không khí rất hiệu quả, có thể đặt trong phòng ngủ giúp bạn có giấc ngủ trong lành hơn.', 125000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0464', 1, 'AGLV004', 'Cây vạn lộc', 'Cây vạn lộc ‘Valentine’ để bàn chậu sứ trắng AGLV004', 'Cây vạn lộc là dòng cây cảnh nội thất dễ chăm sóc, có thể sống được trong điều kiện ánh sáng thấp. Với một hình dạng lá độc đáo, bên trên là những họa tiết đẹp mắt giống như tranh vẽ giúp tạo thêm điểm nhấn cho không gian nội thất.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0465', 1, 'CTBC003-1', 'Trầu bà xanh', 'Cây trầu bà xanh để bàn chậu gốm CTBC004', 'Cây trầu bà để bàn có tác dụng thanh lọc không khí trong nhà mà còn chứa đựng ý nghĩa phong thủy rất tốt, mang dáng vẻ thanh lịch và sang trọng.', 140000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0466', 1, 'SCHE002', 'Ngũ gia bì', 'Cây ngũ gia bì mini chậu đất nung SCHE005', 'Cây ngũ gia bì còn được gọi là cây Chân chim, có dáng đẹp, sống khỏe, dễ uốn tỉa nên thường được trồng làm cây cảnh văn phòng, trang trí trong nhà.', 140000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0467', 1, 'phil004', 'Hồng hạc cam', 'Cây Hạc cam chậu xi măng đá mài Philodenron Billietiae PHIL004', 'Với chiếc lá gây ấn tượng khá mạnh với nhiều người chơi kiểng nên hiện tại Billietiae đang dần phổ biến tại Việt Nam.', 5000000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0468', 1, 'LONI010', 'Cây kim ngân', 'Cây kim ngân để bàn chậu sứ tuổi Tuất LONI010', 'Kim ngân là loại cây mang ý nghĩa may mắn, tài lộc và thường được làm quà biếu trong các dịp khai trương, tân gia, lên chức… như lời cầu chúc vạn sự may mắn, tấn tài tấn lộc đến người nhận.Chọn vị trí đặt cây cũng khá quan trọng để thu hút sự thịnh vượng và may mắn cho gia chủ.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0469', 1, 'AGLA003', 'Cây Phú Quý', 'Cây Phú quý để bàn chậu gốm Aglaonema Siam Aurora chậu sứ để bàn AGLA003', 'Cây Phú Quý có ý nghĩa mang lại may mắn, phú quý, đại cát đại lợi, bình an cho chủ nhân. Để Trúc Phú Quý mang lại ý nghĩa trọn vẹn bạn lưu ý về vị trí đặt theo hướng hợp với bản mệnh của mình.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0470', 1, 'MONS006', 'Trầu bà lá xẻ', 'Cây Trầu bà Nam Mỹ Monstera nhiều thân chậu gốm đen MONS006', 'Là loài cây sở hữu cho mình những chiếc lá được tạo nét như trái tim, càng lớn lá bắt đầu xuất hiện các đường xẻ một cách ngẫu nhiên.', 650000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0471', 1, 'CTBH004', 'Trúc bách hợp', 'Cây trúc bách hợp chậu xi măng xám CTBH004', 'Cây trúc bách hợp thường được làm quà tặng nhân dịp khánh thành, khai trương, tân gia, như là lời chúc may mắn đến với người nhận.', 750000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0472', 1, 'mons005', 'Trầu bà lá xẻ', 'Cây Trầu bà Nam Mỹ Monstera nhiều thân chậu xi măng MONS005', 'Là loài cây sở hữu cho mình những chiếc lá được tạo nét như trái tim, càng lớn lá bắt đầu xuất hiện các đường xẻ một cách ngẫu nhiên.', 1200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0473', 1, 'CNN002', 'Cây ngọc ngân', 'Cây Ngọc Ngân để bàn chậu gốm size Trung – Aglaonema Snow White CNN002', 'Cây ngọc ngân thuộc dòng thân thảo, họ nhà Araceae, lá hình bầu, thuôn nhọn dần về đuôi. Lá cây mềm, màu xanh đốm trắng khá đặc trưng, viền lá màu xanh đậm và mướt. Cây ngọc ngân thuộc giống cây ưa bóng và sinh trưởng mạnh ở nơi mát mẻ rất thích hợp trồng cửa sổ phòng ngủ, phòng khách và trang trí cho cảnh quan văn phòng, quán cafe.', 450000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0474', 1, 'ancr001', 'Hồng môn lá tim', 'Cây Hồng môn Anthurium Crystallinum để bàn ANCR001', 'Crystallinum đẻ nhánh, tốc độ ra lá và để nhánh nhanh nhưng size lá không quá to.', 1200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0475', 1, 'CNN001', 'Cây ngọc ngân', 'Cây ngọc ngân để bàn chậu đất nung AGLA004', 'Cây ngọc ngân là là cây trong nhà được đánh giá cao về phong thuỷ, người ta quan niệm rằng cây Ngọc ngân mang đến tài lộc, may mắn khi đặt trên bàn làm việc hay trang trí trong văn phòng. Về mặt tinh thần cây Ngọc ngân rất thích hợp làm quà tặng tình yêu.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0476', 1, 'TBTX005', 'Cây trầu bà thanh xuân / Cây trầu bà tay Phật', 'Cây trầu bà thanh xuân lớn chậu sứ trắng TBTX005', 'Trầu bà thanh xuân là loại cây trong nhà độc đáo được mọi người ưa thích và trưng bày trang trí. Trầu Bà Thanh Xuân có ý nghĩa về mặt phong thủy nó còn có ý nghĩa may mắn, đem lại tài lộc, gia tăng vận khí, thể hiện ý chí vươn lên của gia chủ.', 650000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0477', 1, 'CATH065', 'Đuôi công sọc', 'Cây Đuôi công sọc Calathea Vittata chậu gốm CATH065', 'Nếu bạn đang tìm một chiếc cây để bàn thật bắt mắt hay một món quà khai trương văn phòng mới cho những người bạn trân trọng thì đừng ngần ngại mà chọn ngay một chiếc Đuôi Công xinh xắn này nhé.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0478', 1, 'TBTX001', 'Cây trầu bà thanh xuân / Cây trầu bà tay Phật', 'Cây trầu bà thanh xuân cỡ lớn chậu ươm TBTX002', 'Trầu bà thanh xuân là loại cây trong nhà độc đáo được mọi người ưa thích và trưng bày trang trí. Trầu Bà Thanh Xuân có ý nghĩa về mặt phong thủy nó còn có ý nghĩa may mắn, đem lại tài lộc, gia tăng vận khí, thể hiện ý chí vươn lên của gia chủ.', 550000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0479', 1, 'ALOCA001', '• Môn Rồng Bạc\r\n• Alocasia Baginda\r\n• Silver Dragon', 'Cây Môn Rồng Bạc Alocasia Baginda Silver Dragon chậu sứ trắng  ALOCA001', 'Một viên ngọc đến từ họ nhà Alocasia, kích thích mọi ánh nhìn, Alocasia Baginda Silver Dragon nổi tiếng và đầy mê hoặc. Nó có thể tồn tại khá tốt trong nhà lẫn ngoài trời.', 650000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0480', 1, 'CDBD001', 'Đa búp đỏ', 'Cây đa búp đỏ 3 thân chậu sứ trắng CDBD001', 'Từ lâu cây Đa luôn là biểu tượng của sự trường tồn, sức sống dẻo dai, nơi chứa đựng thần quyền và tâm linh của con người. Chính lẽ đó mà trong phong thủy, Đa Búp Đỏ cũng mang ý nghĩa của sự may mắn, an lành.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0481', 1, 'dief001', 'Sao sáng', 'Cây Sao sáng Dieffenbachia Camille chậu sứ để bàn DIEF001', 'Có nguồn gốc từ vùng Caribê và Nam Mỹ, chúng được trồng rộng rãi để làm cây trang trí.', 450000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0482', 1, 'agla002', 'Cây Phú Quý', 'Cây Phú quý Aglaonema Siam Aurora chậu sứ để bàn AGLA002', 'Cây Phú Quý có ý nghĩa mang lại may mắn, phú quý, đại cát đại lợi, bình an cho chủ nhân. Để Trúc Phú Quý mang lại ý nghĩa trọn vẹn bạn lưu ý về vị trí đặt theo hướng hợp với bản mệnh của mình.', 390000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0483', 1, 'SCHE026', 'Ngũ gia bì cẩm thạch', 'Cây Ngũ gia bì cẩm thạch bonsai chậu sứ SCHE026', 'Còn được gọi là cây Chân chim, có dáng đẹp, sống khỏe, dễ uốn tỉa nên thường được trồng làm cây cảnh văn phòng, trang trí trong nhà.', 450000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0484', 1, 'ZAMI017', 'Cây kim tiền / Kim phát tài', 'Cây kim tiền cỡ trung chậu xi măng trụ vuông ZAMI017', 'Cây kim tiền là loài thực vật họ nhà Araceae, có lá hình xương cá màu xanh mướt. Loài này rất dễ sống phát triển mạnh ở những nơi có độ ẩm cao, thường được trồng để trang trí trong phòng khách, làm quà tặng tân gia, khai trương cửa hàng. Chúng được đặt tại trước đại sảnh, lối ra vào cổng chính hoặc trưng bày vào những dịp đặc biệt.', 750000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0485', 1, 'cala004', 'Đuôi công lá tím', 'Cây đuôi công lá tím sọc xanh Calathea Bachemiana để bàn CALA004', 'Có lá thuông dài, bề mặt dưới lá màu tím. Chúng thường được gọi là calatheas hoặc cây cầu nguyện.', 390000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0486', 1, 'cupr001', 'Tùng thơm', 'Cây Tùng thơm Cupressus Macrocarpa chậu xi măng nhỏ để bàn CUPR001', 'Còn được biết đến với cái tên Tùng chanh', 420000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0487', 1, 'cala003', 'Đuôi công táo xanh', 'Cây đuôi công táo xanh Calathea Orbifolia chậu gốm để bàn CALA003', 'Với tán lá tròn trịa xoè rộng phủ xanh cả khu vườn nhỏ, bạn chỉ cần trồng vài cây Orbifolia là không khí tropical mát mẻ đã về với không gian nhà rồi đó.', 400000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0488', 1, 'CPTDL002', 'Cây phát tài đài loan', 'Cây phát tài Đài Loan chậu trụ sứ CPTDL002', 'Cây phát tài Đài Loan lá dài màu xanh sọc vàng, mặt lá bóng, mép lá nguyên, bẹ lá ôm sát thân. Thân gỗ nhỏ\r\nsống lâu năm, chịu bóng râm tốt, sống được ngoài trời. Phát triển chậm và ít khi phân nhánh ngang', 680000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0489', 1, 'mons004', 'Trầu bà lỗ', 'Cây Trầu bà lỗ leo cột chậu gốm trắng MONS004', 'Có khả năng sống trong bóng râm, dễ thích nghi với nhiều điều kiện khác nhau, đặc biệt là những ngóc ngách ít ánh sáng như môi trường trong nhà.', 700000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0490', 1, 'LYRA022', 'Cây bàng Singapore', 'Cây bàng Singapore lớn chậu gốm sứ LYRA022', 'Bàng Singapore là loại cây nội thất đang được ưa chuộng nhất hiện nay bởi cây có khả năng cải thiện chất lượng không khí mang lại sự xanh mát và sang trọng cho không gian.', 2200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06');
