('CCNPLT0662', 1, '', 'Cây thu hải đường cánh thiên thần', 'Cây thu hải đường cánh thiên thần chậu ươm BEGO001', 'Trong họ Thu Hải Đường này thì loài lá đốm là loài nổi tiếng nhất và được trồng nhiều nhất. Hoa của chúng màu trắng hoặc hồng khá bền màu và bền hoa', 240000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0663', 1, '', '• Môn nhung lá xanh\r\n• Alocasia Frydek', 'Cây Môn Nhung Alocasia Micholitziana Frydek', 'Cây Alocasia Micholitziana Frydek hay còn gọi là Alocasia Green Velvet, là một giống môn đang khá “hot” trong vài năm trở lại đây. Môn nhung Frydek biết cách làm cho ngôi nhà, khu vườn của bạn sinh động thêm với những chiếc lá rộng và mượt như nhung, kèm theo đó là những đường gân trắng nổi bật.', 200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0664', 1, '', '• Alocasia Black Velvet\r\n• Môn nhung lá đen', 'Cây Môn Nhung Lá Đen Alocasia Black Velvet', 'Cây Môn Nhung Lá Đen Alocasia Black Velvet hay còn gọi là Little Queen Alocasia, xuất thân từ các khu rừng rậm Đông Nam Á, trước đây khá hiếm nhưng đang dần phổ biến hơn nhờ công nghệ nuôi cấy mô.', 200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0665', 1, '', '• Môn Rồng Bạc\r\n• Alocasia Baginda\r\n• Silver Dragon', 'Cây Môn Rồng Bạc Alocasia Baginda Silver Dragon', 'Một viên ngọc đến từ họ nhà Alocasia, kích thích mọi ánh nhìn, Alocasia Baginda Silver Dragon nổi tiếng và đầy mê hoặc. Nó có thể tồn tại khá tốt trong nhà lẫn ngoài trời.', 200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0666', 1, 'AGLA025', 'Cây vạn lộc', 'Cây vạn lộc phát chậu men sứ AGLA025', 'Cây vạn lộc là dòng cây cảnh nội thất dễ chăm sóc, có thể sống được trong điều kiện ánh sáng thấp. Với một hình dạng lá độc đáo, bên trên là những họa tiết đẹp mắt giống như tranh vẽ giúp tạo thêm điểm nhấn cho không gian nội thất.', 320000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0667', 1, '', '• Alocasia Polly\r\n• Môn Quan Âm', 'Cây Môn Quan Âm Alocasia Amazonica Polly', 'Cây Alocasia Amazonica Polly ở Việt Nam người đam mê kiểng lá hay gọi là cây Môn Quan Âm, có sức sống khỏe, dễ trồng, dễ chăm sóc, thường được trồng trong chậu trang trí trong nhà lẫn ngoài trời.', 200000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0668', 1, '', 'Cây Hoa Sen', 'Cây Hoa Sen – Cao 37cm', '', 140000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0669', 1, '', 'Cây Thủy Cúc', 'Cây Bách Thủy Tiên/ Thủy Cúc/ Từ Cô Lá Tim – Cao 50cm', 'Cây Bách Thủy Tiên khởi nguồn từ Bắc Mỹ và miền bắc của Nam Mỹ, hay còn được gọi với cái tên Thủy Cúc, Từ Cô Lá Tim, tên khoa học là Echinodorus cordifolius thuộc họ thực vật Alismataceae.', 95000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0670', 1, '', 'Cây Lẻ Bạn', 'Cây Lẻ Bạn – Cao 19cm', '', 30000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0671', 1, 'CHPD003', 'Huyết phất dụ', 'Cây huyết dụ đỏ chậu ươm CHPD003', 'Cây huyết dụ là loại cây thân gỗ mọc theo bụi thường xanh, có màu đỏ hồng, dáng thân và tán lá rất đẹp thu hút người nhìn.', 135000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0672', 1, 'SCHE010', 'Ngũ gia bì cẩm thạch', 'Cây ngũ gia bì cẩm thạch chậu ươm SCHE010', 'Ngũ gia bì là loại cây mang lại tài vận, thịnh vượng và may mắn, trồng trong nhà sẽ mang đến một không gian thư thái, trong lành cho gia đình.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0673', 1, 'MONS007', 'Cây Trầu Bà Lỗ', 'Cây Trầu Bà Lỗ Monstera adansonii Monkey Mask – MONS007', 'Monstera adansonii là cây thân thảo, thích nghi với bóng râm hoàn toàn, vì vậy chúng thường được trồng trong nhà, sức sống mạnh mẽ, dễ dàng chăm sóc và có thể tồn tại trong nhiều môi trường khác nhau, gốc nhiều nhánh và đốt.\r\nVới những chiếc lá xanh tươi, thân hình nhỏ nhắn trông rất dễ thương có thể sẽ rất phù hợp với các bạn nữ, nhân viên văn phòng trong lúc mệt mỏi có thể ngắm nhìn để giải tỏa phần nào áp lực công việc, cuộc sống.', 165000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0674', 1, 'CRYP001', 'Cây dứa hồng phụng / Lưỡi rồng', 'Cây dứa hồng phụng Cryptanthus ‘Elaine’ chậu ươm CRYP001', 'Với hoa văn độc đáo và màu sắc sặc sỡ, cây dứa hồng phụng rất thích hợp làm cây để bàn, hoặc là món quà tặng ý nghĩa nhân dịp lễ tết, mừng khai trương, thăng chức. Nếu trồng trong nhà, cây mang ý nghĩa giúp không khí tích cực, tăng cường tình yêu gia đình.', 80000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0675', 1, '', 'Cây cọ Nhật / Kè Nhật', 'Cây cọ nhật nhỏ chậu ươm LICU001', 'Cọ Nhật là loại cây sở hữu bộ lá vừa to vừa xanh, chỉ cần trồng một cây là đủ để phủ xanh không gian sống. Bên cạnh đó loại cây này trong thủy được xem là có tác dụng sinh tài, giữ của, và giúp tạo nên sự sang trọng bởi vẻ ngoài thanh lịch của chúng.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0676', 1, 'CTBH001', 'Cây trúc bách hợp', 'Cây trúc bách hợp một thân Dracaena reflexa CTBH001', '', 450000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0677', 1, 'CPTDL001', 'Cây Phát Tài Đài Loan', 'Cây Phát Tài Đài Loan – Cao 70 Cm – Bầu ươm CPTDL001', '', 280000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0678', 1, '', 'Cây Cung Điện Vàng', 'Cây Cung Điện Vàng – Chậu Đất Nung', 'Cây Cung Điện Vàng được tìm thấy ở những khu rừng khí hậu nhiệt đới gió mùa, thành ra chúng phát triển hoàn hảo với sinh nhưỡng của Việt Nam. Cây vô cùng khỏe, ưa môi trường mát có độ ẩm cao vì vậy cây cung điện là lựa chọn hoàn hảo cho decor phòng khách hoặc phòng ngủ hiện đại.', 220000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0679', 1, 'LYRA024', 'Cây bàng Singapore / Sung tỳ bà', 'Cây bàng Singapore một thân chậu xi măng LYRA024', 'Cây Bàng Singapore Lớn có thể dễ dàng nhận ra ở những góc quán cafe, bàn làm việc công. Với những chiếc lá căng bóng hình đàn vĩ cầm rất lớn, nhiều gân là hình chân chim nổi bật và sức sống rất mạnh mẽ.', 400000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0680', 1, '', 'Cây Phát Tài Huy Hoàng/ Phát Tài Núi', 'Cây Phát Tài Huy Hoàng/ Phát Tài Núi – Cao 60cm', '', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0681', 1, 'ASPL140', 'Ráng ổ phụng', 'Cây ráng tổ phụng nhỏ chậu ươm ASPL140', 'Ráng ổ phụng là loại cây thuộc họ dương xỉ, có sức sống rất mãnh liệt, loài này có hình hài rất giống với tổ chim nên nó được xem là biểu tượng cho sự ấm áp, hạnh phúc và xum vầy trong gia đình.', 140000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0682', 1, '', 'Cây Lan Thanh', 'Cây Lan Thanh – Cao 40cm', '', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0683', 1, '', 'Cây Ngân Hậu', 'Cây Ngân Hậu Giống Mới – Cao 41 Cm', '', 125000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0684', 1, 'ZAMI004', 'Cây kim tiền / Cây kim phát tài', 'Cây kim tiền để bàn chậu sứ ZAMI004', 'Cây kim tiền được đánh giá rất cao là loại cây phong thủy đem đến tài lộc, thịnh vượng, giàu sang, sức khỏe cho gia chủ.Đặc biệt khi kim tiền nở hoa gia chủ càng đắc lộc.', 150000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0685', 1, '', 'Cây Lan Tim Nhí/ Cây Khúc Thủy', 'Cây Lan Tim Nhí/ Cây Khúc Thủy – Cao 10cm', '', 105000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0686', 1, 'CPTK004', 'Cây phát tài khúc', 'Cây phát tài khúc bộ 3 chậu sứ trắng CPTK004', 'Loài cây nội thất được đánh giá là đem lại nhiều sinh khí, may mắn và thịnh vượng cho gia chủ, nhất là khi cây nở hoa là dấu hiệu tiền tài đang đến với bạn. Hơn nữa, nếu bạn đặt cây theo hướng Đông hay Đông Nam của ngôi nhà thì sẽ đem tới nhiều may mắn bởi cây là đại diện cho hành Mộc.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0687', 1, '', 'Cây Trúc Nhật Vàng', 'Cây Trúc Nhật Vàng – Cao 20cm', '', 75000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0688', 1, 'PEOB004', 'Cây trường sinh cẩm thạch', 'Cây trường sinh cẩm thạch chậu đất nung PEOB004', 'Cây trường sinh cẩm thạch là một loại cây cảnh trong nhà đẹp mắt, với bộ lá luôn xanh mát, sức sống mãnh liệt, dễ chăm sóc. Đây cũng là loại cây phong thủy với tác dụng mang đến sức khỏe, may mắn và tiền tài tới cho gia chủ. Người ta thường hay tặng cây trường sinh vào những dịp đặc biệt như là một lời chúc phúc tới người nhận.', 160000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0689', 1, 'ZAMI005', 'Cây kim tiền', 'Cây kim tiền chậu đất nung họa tiết thổ cẩm ZAMI005', 'Cây kim tiền rất dễ sống vì có thể trồng tại nơi có ít ánh sáng, và nó cũng không cần đến quá nhiều nước để phát triển. Loại cây này thường được lựa chọn để đặt trong nhà để tạo thêm mảng xanh, đồng thời được trồng với tác dụng phong thủy.', 185000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0690', 1, 'ZAMI009', 'Cây kim tiền', 'Cây kim tiền để bàn chậu men sứ ZAMI009', 'Cây kim tiền hay kim phát tài là loại cây phong thủy rất được ưu chuộng và lựa chọn, với mong muốn công việc làm ăn luôn gặp thuận lợi, dù to nhỏ cũng đều suôn sẻ như sức sống của cây vậy.', 420000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0691', 1, '', 'Cây Rau Má Kiểng/ Cỏ Đồng Tiền', 'Cây Rau Má Kiểng/ Cỏ Đồng Tiền – Cao 18cm', '', 45000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0692', 1, 'AGLA050', 'Phú quý đỏ', 'Cây phú quý viền đỏ ‘Red Siam’ chậu ươm AGLA050', 'Aglaonema Red là loại cây trong nhà có thân màu trắng hồng, lá màu xanh viền đỏ đẹp mắt, Loài này rất dễ sống, thường được trồng để trang trí trong nhà, thanh lọc không khí và cũng là loại cây phong thủy, tượng trưng cho tài lộc và may mắn.', 75000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0693', 1, 'AGLA001', 'Cây ngọc ngân', 'Cây ngọc ngân ‘Snow White’ chậu ươm AGLA001', 'Cây ngọc ngân là là cây trong nhà được đánh giá cao về phong thuỷ, người ta quan niệm rằng cây Ngọc ngân mang đến tài lộc, may mắn khi đặt trên bàn làm việc hay trang trí trong văn phòng. Về mặt tinh thần cây Ngọc ngân rất thích hợp làm quà tặng tình yêu.', 75000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0694', 1, '', 'Cây ngọc ngân', 'Cây Càng Cua Cẩm Thạch Peperomia scandens Variegata – Cao 16 Cm', 'Cây Càng Cua Cẩm Thạch Peperomia scandens Variegata dây leo mỏng manh, có lá màu xanh nhạt, trắng sữa, hình trái tim và cuối cùng rụng xuống khi chúng trưởng thành. Một loại cây biểu trưng cho vui vẻ, nhẹ nhàng và tươi tốt chắc chắn sẽ mang lại nụ cười cho bạn.', 85000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0695', 1, 'ANTH001', 'Cây hồng môn', 'Cây hồng môn nhỏ để bàn chậu ươm ANTH001', 'Cây hồng môn được biết tới là một loại cây cảnh mang lại điều tốt lành, có thể giúp điều hòa khí phong thủy trong nhà, có khả năng thu hút những dòng khí tích cực và làm tiêu bớt dòng khí tiêu cực cho môi trường xung quanh, trồng cây hồng môn trong nhà sẽ giúp không gian sống trở nên hài hòa và bình yên hơn.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0696', 1, '', 'Cây Hoa Lan Quân Tử', 'Cây Hoa Lan Quân Tử – Cao 38cm', '', 220000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0697', 1, 'PEAC001', 'Cây lan ý / Cánh buồm / Huệ hòa bình', 'Cây lan ý nhỏ chậu ươm PEAC001', 'Cây lan ý thuộc giống cây rất dễ sống, thường được trồng để trang trí trong nhà cần ít ánh sáng, thanh lọc không khí. Nó cũng loại bỏ nhiều chất độc từ không khí trong nhà bao gồm cả formaldehyde và amoniac. Loại cây này còn mang ý nghĩa đem lại may mắn, thành công và thịnh vượng, hợp với người mệnh Kim và Thủy', 140000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0698', 1, 'LYRA011', 'Sung tì bà, sung tỳ bà hay sung lá đàn', 'Cây bàng Singapore mini để bàn chậu đất nung LYRA011', 'Cây bàng Singapore đang loại cây trong nhà được nhiều người ưu chuộng. Với bộ lá màu xanh lá mượt mà, dáng thẳng đứng thanh lịch và sang trọng giúp cho không gian nhà bạn thêm phần sinh động. Cây cũng có khả năng thanh lọc không khí giúp không gian thêm trong lanh.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0699', 1, 'HAWA001', 'Cau Hawaii', 'Cây cau Hawaii lớn chậu ươm HAWA001', 'Cau Hawaii là một loại cây cây cảnh nội thất phổ biến, thích hợp để trưng bày trong các không gian nội thất sang trọng. Loại cây này có sức sống mãnh liệt, dễ chăm sóc, có bộ lá xanh mướt và mang ý nghĩa rất tốt trong phong thủy.', 320000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0700', 1, 'PEOB001', 'Cây trường sinh', 'Cây trường sinh để bàn chậu ươm PEOB001', 'Cây trường sinh là một loại cây cảnh trong nhà đẹp mắt, với bộ lá luôn xanh mát, sức sống mãnh liệt, dễ chăm sóc. Đây cũng là loại cây phong thủy với tác dụng mang đến sức khỏe, may mắn và tiền tài tới cho gia chủ. Người ta thường hay tặng cây trường sinh vào những dịp đặc biệt như là một lời chúc phúc tới người nhận.', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0701', 1, '', 'Cây Vạn Lộc/Thiên Phú', 'Cây Vạn Lộc/Thiên Phú', '', 115000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0702', 1, 'HEDE001', 'Cây thường xuân', 'Cây thường xuân xanh chậu treo HEDE001', 'Cây thường xuân có bộ lá xanh tươi tốt, hình dáng ấn tượng giúp mang lại không gian xanh mát. Đây là dòng cây dễ chăm đang là “trend” được nhiều người lựa chọn trồng trang trí trong nhà rất đẹp.', 160000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0703', 1, 'DVX001', 'Cây Trầu Bà Đế Vương Xanh', 'Cây Trầu Bà Đế Vương Xanh Để Bàn DVX001', 'Trầu Bà Đế Vương có tên tiếng Anh là Philodendron Imperial thuộc họ Araceae (Ráy). Nguồn gốc của cây từ đảo Solomon, nguyên sinh ở Indonesia, là dòng cây thân thảo, trong tự nhiên dòng cây này có thể cao hơn 1.5m, nhưng với mục đích trang trí thì MOW sẽ gửi đến các bạn cây với kích cỡ nhỏ để tiện đặt ở bất cứ đâu mình thích.', 350000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0704', 1, 'PODO002', 'Tùng la hán', 'Cây tùng la hán thủy sinh để bàn PODO002', 'Cây tùng trong phong thủy được coi là loài cây quý có linh khí rất tốt bởi chúng có thể sống hàng trăm năm, giúp cản gió độc, xua đuổi tà khí, tránh những điều xui xẻo và đem lại sự trường thọ gia chủ.', 140000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0705', 1, 'CDBD004', 'Đa búp đỏ', 'Cây đa búp đỏ để bàn 3 thân chậu sứ trắng CDBD004', 'Từ lâu cây Đa luôn là biểu tượng của sự trường tồn, sức sống dẻo dai, nơi chứa đựng thần quyền và tâm linh của con người. Chính lẽ đó mà trong phong thủy, Đa Búp Đỏ cũng mang ý nghĩa của sự may mắn, an lành.', 230000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0706', 1, 'RADE001', 'Cây hạnh phúc', 'Cây hạnh phúc để bàn chậu ươm RADE001', 'Cây dễ sinh trưởng phát triển mọi diều kiện thời tiết khác nhau nên rất dễ trồng và chăm sóc. Cây sống tốt trong điều kiện có điều hoà và môi trường thiếu sáng nên chúng ta có thể dễ dàng lựa chọn cây trang trí để bàn cho không gian làm việc trở nên xanh mát và ấm áp.', 250000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0707', 1, 'SPID001', 'Cây cỏ lan chi / cây dây nhện', 'Cây cỏ lan chi để bàn chậu ươm SPID001', 'Cỏ Lan Chi (hay còn gọi là Cỏ Nhện) là loại cây rất dễ sống với điều kiện khí hậu nóng tại Việt Nam. Đặc biệt, nó nằm trong top các loại cây lọc không khí rất hiệu quả, có thể đặt trong phòng ngủ giúp bạn có giấc ngủ trong lành hơn.', 30000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0708', 1, 'CASA001', 'Calathea ornata Sanderiana', 'Cây đuôi công sọc Calathea Sanderiana chậu ươm CASA001', 'Calathea ornata Sanderiana là loài thực vật thân thảo, có lá hình thuôn tròn, sọc trắng xanh. Loài này rất dễ sống, thường được trồng để trang trí trong nhà, thanh lọc không khí và mang ý nghĩa đem lại may mắn, thành công và thịnh vượng.', 160000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0709', 1, '', 'Cây trầu bà cẩm thạch', 'Cây Trầu Bà Lá Sữa Epipremnum pinnatum NJoy', 'Cây Trầu Bà Sữa hay còn gọi là Trầu Bà Cẩm Thạch, là một người “anh em” của cây Trầu Bà thông thường nên nó sở hữu những đặc điểm và tính chất tương tự. Nó có tên khoa học là Epipremnum ‘Marble Queen’ hay có thể gọi với tên tiếng Anh là Epipremnum pinnatum ‘NJoy’, thuộc họ Ráy (Araceae), có nguồn gốc từ miền bắc Australia, Malaysia.', 125000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0710', 1, 'CATH001', 'Cây đuôi công táo xanh', 'Cây đuôi công táo xanh Calathea Orbifolia chậu ươm CATH001', 'Cây đuôi công táo xanh là loài thực vật thân thảo, có lá hình thuôn tròn, sọc trắng xanh. Hay được gọi với tên là Cây Đuôi Công Táo Xanh. Loài này rất dễ sống, thường được trồng để trang trí trong nhà, thanh lọc không khí và mang ý nghĩa đem lại may mắn, thành công và thịnh vượng.', 180000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0711', 1, '', 'Cây hoàng hậu chậu sứ', 'Cây hoàng hậu chậu sứ', 'Cây Hoàng Hậu có tên khoa học Aglaonema Diamond Bay, thuộc họ Araceae (ráy) có hoa, dòng Aglaonema phổ biến với các loại cây như: Vạn Lộc, Giữ Tiền, Bạch Mã Hoàng Tử, Phú Quý, Ngọc Ngân…Lá có chiều rộng trung bình khoảng 10cm, dài 20cm, rộng ở giữa và thuôn dần ở phía đầu, mặt sau của lá có màu xanh, lá mềm, nhẵn không có răng cưa hay gai.', 270000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0712', 1, 'PHIG003', 'Trầu bà đế vương xanh', 'Cây Trầu Bà Đế Vương Xanh Thủy Sinh ‘Imperial Green’ PHIG003', 'Trầu Bà Đế Vương Xanh (Philodendron Imperial Green) là một giống cây rất ưu chuộng để trồng với ý nghĩa phong thủy mà nó mang lại. Nên đặt cây tại các vị trí như trong phòng khách, phòng làm việc, văn phòng hoặc sảnh ra vào cơ quan.', 185000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0713', 1, '', 'Ngũ gia bì cẩm thạch', 'Cây ngũ gia bì xanh mini chậu ươm SCHE001', 'Ngũ gia bì là loại cây mang lại tài vận, thịnh vượng và may mắn, trồng trong nhà sẽ mang đến một không gian thư thái, trong lành cho gia đình.', 90000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0714', 1, 'LYRA001', 'Cây bàng Singapore mini', 'Cây bàng Singapore mini chậu ươm LYRA001', 'Cây bàng Singapore mini là dòng cây cảnh mini có những chiếc lá nhỏ cừng cáp đẹp mắt, rất phù hợp để trồng trên bàn làm việc, kệ sách hoặc trang trí tiểu cảnh. Cây bàng Singapore cũng được trồng với tác dụng phong thủy như là sự may mắn cho gia chủ.', 90000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0715', 1, 'TBDB', 'Cây Trầu Bà', 'Cây Trầu Bà Để Bàn TBDB', 'Cây trầu bà có ý nghĩa phong thủy mang lại những điều may mắn, hạnh phúc và ấm êm cho gia chủ. Bên cạnh đó cây trầu bà cũng là loại cây vô cùng dễ dàng chăm sóc và phát triển. Rất thích hợp được trang trí trên bàn làm việc, treo cửa sổ, bancony, góc phòng…', 120000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0716', 1, 'LYRA010', 'Cây bàng Singapore', 'Cây bàng Singapore mini để bàn chậu sứ LYRA010', 'Bàng Singapore đang loại cây trong nhà được nhiều người ưu chuộng. Với bộ lá màu xanh lá mượt mà, dáng thẳng đứng thanh lịch và sang trọng giúp cho không gian nhà bạn thêm phần sinh động.', 160000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
('CCNPLT0717', 1, 'ZAMI003', 'Cây kim tiền / Cây kim phát tài', 'Cây kim tiền kích thước lớn chậu ươm ZAMI003', 'Cây kim tiền được đánh giá rất cao là loại cây phong thủy đem đến tài lộc, thịnh vượng, giàu sang, sức khỏe cho gia chủ.Đặc biệt khi kim tiền nở hoa gia chủ càng đắc lộc.', 420000, NULL, NULL, NULL, 1, '2024-11-20 00:49:06', '2024-11-20 00:49:06'),
